style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Poppins", sans-serif;
}

.top-nav {
	background: #ffffff;
	padding: 10px 5%;
	display: flex;
	justify-content: space-between;
	align-items: center;
	box-shadow: 0 2px 15px rgba(0, 0, 0, 0.1);
}

.logo {
	font-size: 28px;
	font-weight: 700;
	color: #2ecc71;
}

.nav-items {
	display: flex;
	align-items: center;
	gap: 30px;
}

.nav-links {
	display: flex;
	gap: 25px;
}

.nav-links a {
	text-decoration: none;
	color: #34495e;
	font-weight: 500;
	transition: color 0.3s ease;
}

.nav-links a:hover {
	color: #2ecc71;
}

.auth-buttons {
	display: flex;
	gap: 15px;
}

.auth-btn {
	padding: 8px 25px;
	border-radius: 20px;
	text-decoration: none;
	font-weight: 500;
}

.sign-in {
	border: 2px solid #2ecc71;
	color: #2ecc71;
}

.sign-up {
	background: #2ecc71;
	color: white;
}

.hero {
	padding: 100px 5% 60px;
	background: linear-gradient(rgba(255, 255, 255, 0.9), rgba(255, 255, 255, 0.9)), url("https://picsum.photos/1600/900?food");
	background-size: cover;
	position: relative;
}

.hero-content {
	max-width: 1200px;
	margin: 0 auto;
	text-align: center;
}

.hero-title {
	font-size: 3.5rem;
	color: #2c3e50;
	margin-bottom: 20px;
}

.hero-subtitle {
	color: #7f8c8d;
	font-size: 1.2rem;
	margin-bottom: 40px;
	max-width: 800px;
	margin-left: auto;
	margin-right: auto;
}

.search-container {
	max-width: 600px;
	margin: 0 auto 50px;
	position: relative;
}

.search-input {
	width: 100%;
	padding: 18px 30px;
	border: 2px solid #ecf0f1;
	border-radius: 30px;
	font-size: 1rem;
	padding-right: 150px;
}

.search-btn {
	position: absolute;
	right: 2px;
	top: 2px;
	padding: 18px 30px;
	background: #2ecc71;
	color: white;
	border: none;
	border-radius: 25px;
	cursor: pointer;
	font-weight: 500;
	font-size: 1rem;
}

.features-grid {
	display: grid;
	grid-template-columns: repeat(4, 1fr);
	gap: 20px;
	max-width: 1200px;
	margin: 0 auto;
}

.feature-card {
	background: white;
	padding: 25px;
	border-radius: 15px;
	box-shadow: 0 5px 25px rgba(0, 0, 0, 0.1);
	text-align: center;
	transition: transform 0.3s ease;
}

.feature-card:hover {
	transform: translateY(-10px);
}

.feature-icon {
	font-size: 2.5rem;
	color: #2ecc71;
	margin-bottom: 15px;
}

.categories-section {
	padding: 60px 5%;
	background: #f9f9f9;
}

.categories-grid {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
	gap: 25px;
	max-width: 1200px;
	margin: 0 auto;
}

.category-card {
	background: white;
	padding: 25px;
	border-radius: 15px;
	text-align: center;
	transition: all 0.3s ease;
	cursor: pointer;
}

.category-card:hover {
	box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
}

.category-icon {
	font-size: 2rem;
	color: #2ecc71;
	margin-bottom: 15px;
}

@media (max-width: 1024px) {
	.hero-title {
		font-size: 2.5rem;
	}

	.features-grid {
		grid-template-columns: repeat(2, 1fr);
	}
}

@media (max-width: 768px) {
	.auth-buttons {
		display: none;
	}

	.hero-title {
		font-size: 2rem;
	}

	.search-input {
		padding-right: 30px;
	}

	.search-btn {
		position: relative;
		width: 100%;
		margin-top: 15px;
		right: auto;
		top: auto;
	}

	.features-grid {
		grid-template-columns: 1fr;
	}
}
