style:
/* Reset defaults */
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

html,
body {
	height: 100%;
	font-family: Arial, sans-serif;
}

/* Hero Section Styles */
.hero {
	position: relative;
	background: url("https://picsum.photos/seed/hero/1920/1080") no-repeat center center/cover;
	height: 100vh;
	display: flex;
	align-items: center;
	justify-content: center;
	text-align: center;
	color: #fff;
}

/* Overlay for better contrast */
.overlay {
	position: absolute;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
	background: rgba(0, 0, 0, 0.6);
	z-index: 1;
}

/* Hero content */
.hero-content {
	position: relative;
	z-index: 2;
	max-width: 800px;
	padding: 20px;
	animation: fadeIn 1.5s ease-out;
}

.hero-content h1 {
	font-size: 3.5rem;
	margin-bottom: 20px;
}

.hero-content p {
	font-size: 1.25rem;
	margin-bottom: 30px;
	line-height: 1.5;
}

.hero-content a {
	text-decoration: none;
	background-color: #ff5c5c;
	color: #fff;
	padding: 15px 30px;
	border-radius: 4px;
	transition: background-color 0.3s ease;
}

.hero-content a:hover {
	background-color: #ff1e1e;
}

/* Fade-in animation */
@keyframes fadeIn {
	from {
		opacity: 0;
		transform: translateY(20px);
	}
	to {
		opacity: 1;
		transform: translateY(0);
	}
}

/* Responsive adjustments */
@media (max-width: 768px) {
	.hero-content h1 {
		font-size: 2.5rem;
	}
	.hero-content p {
		font-size: 1rem;
	}
}

@media (max-width: 480px) {
	.hero-content h1 {
		font-size: 2rem;
	}
	.hero-content a {
		padding: 12px 25px;
	}
}
