style:
/* Global Reset */
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

html,
body {
	height: 100%;
	font-family: "Roboto", sans-serif;
	color: #fff;
}

body {
	background-color: #000;
}

/* Hero Section */
.hero {
	position: relative;
	background: url("https://picsum.photos/seed/carrental/1920/1080") no-repeat center center/cover;
	height: 100vh;
	display: flex;
	align-items: center;
	justify-content: center;
}

/* Gradient Overlay */
.hero::before {
	content: "";
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	background: linear-gradient(135deg, rgba(0, 0, 0, 0.8) 0%, rgba(0, 0, 0, 0.3) 100%);
	z-index: 1;
}

/* Hero Content */
.hero-content {
	position: relative;
	z-index: 2;
	max-width: 900px;
	padding: 20px;
	text-align: center;
	animation: fadeIn 1.5s ease-out;
}

.hero-content h1 {
	font-size: 3.5rem;
	margin-bottom: 20px;
	text-shadow: 2px 2px 8px rgba(0, 0, 0, 0.7);
}

.hero-content p {
	font-size: 1.25rem;
	margin-bottom: 30px;
	line-height: 1.5;
	text-shadow: 1px 1px 6px rgba(0, 0, 0, 0.7);
}

/* Search Form */
.search-form {
	display: flex;
	flex-wrap: wrap;
	justify-content: center;
	background: rgba(255, 255, 255, 0.2);
	padding: 20px;
	border-radius: 10px;
	backdrop-filter: blur(5px);
	box-shadow: 0 4px 10px rgba(0, 0, 0, 0.3);
}

.search-form input,
.search-form button {
	margin: 10px;
	padding: 15px;
	border: none;
	border-radius: 5px;
	font-size: 1rem;
}

.search-form input {
	flex: 1 1 200px;
	background: rgba(255, 255, 255, 0.9);
	color: #333;
}

.search-form input::placeholder {
	color: #777;
}

.search-form button {
	background-color: #ff5c5c;
	color: #fff;
	cursor: pointer;
	transition: background-color 0.3s ease;
	flex: 1 1 150px;
}

.search-form button:hover {
	background-color: #e04e4e;
}

/* Responsive Adjustments */
@media (max-width: 768px) {
	.hero-content h1 {
		font-size: 2.8rem;
	}

	.hero-content p {
		font-size: 1rem;
	}

	.search-form {
		flex-direction: column;
	}

	.search-form input,
	.search-form button {
		flex: 1 1 100%;
		margin: 8px 0;
	}
}

/* Fade-In Animation */
@keyframes fadeIn {
	from {
		opacity: 0;
		transform: translateY(20px);
	}
	to {
		opacity: 1;
		transform: translateY(0);
	}
}
