style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", system-ui, -apple-system, sans-serif;
}

:root {
	--primary: #ef4444;
	--secondary: #dc2626;
	--dark: #1f2937;
	--light: #f9fafb;
}

body {
	overflow-x: hidden;
}

.hero {
	min-height: 100vh;
	padding: 120px 5% 60px;
	background: linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)),
		url("https://images.unsplash.com/photo-1574680096145-d05b474e2155?ixlib=rb-1.2.1&auto=format&fit=crop&w=1920&q=80");
	background-size: cover;
	background-position: center;
	position: relative;
	display: flex;
	align-items: center;
}

.hero-content {
	max-width: 1200px;
	margin: 0 auto;
	color: white;
	text-align: center;
}

.hero-title {
	font-size: clamp(2.5rem, 5vw, 4rem);
	line-height: 1.1;
	margin-bottom: 1.5rem;
	animation: slideUp 1s ease-out;
}

.hero-subtitle {
	font-size: 1.2rem;
	margin-bottom: 2.5rem;
	opacity: 0.9;
	max-width: 600px;
	margin: 0 auto 2.5rem;
}

.cta-button {
	display: inline-flex;
	align-items: center;
	padding: 1rem 2.5rem;
	background: var(--primary);
	color: white;
	border: none;
	border-radius: 0.5rem;
	font-weight: 600;
	text-decoration: none;
	transition: transform 0.3s ease;
	animation: pulse 2s infinite;
}

.cta-button:hover {
	transform: translateY(-3px);
}

.stats-grid {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
	gap: 2rem;
	max-width: 800px;
	margin: 4rem auto 0;
}

.stat-card {
	padding: 1.5rem;
	background: rgba(255, 255, 255, 0.1);
	border-radius: 1rem;
	backdrop-filter: blur(5px);
}

.stat-number {
	font-size: 2rem;
	font-weight: 700;
	color: var(--primary);
	margin-bottom: 0.5rem;
}

@keyframes slideUp {
	from {
		opacity: 0;
		transform: translateY(50px);
	}
	to {
		opacity: 1;
		transform: translateY(0);
	}
}

@keyframes pulse {
	0%,
	100% {
		transform: scale(1);
	}
	50% {
		transform: scale(1.05);
	}
}

.programs-grid {
	display: grid;
	grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
	gap: 2rem;
	margin-top: 4rem;
}

.program-card {
	border: 1px solid white;
	padding: 2rem;
	border-radius: 1rem;
	text-align: center;
	transition: transform 0.3s ease;
}

.program-card:hover {
	transform: translateY(-10px);
}

.program-icon {
	width: 60px;
	height: 60px;
	background: var(--primary);
	border-radius: 50%;
	margin: 0 auto 1rem;
	display: grid;
	place-items: center;
	color: white;
	font-size: 1.5rem;
}

@media (max-width: 768px) {
	.stats-grid {
		grid-template-columns: 1fr;
	}

	.programs-grid {
		grid-template-columns: 1fr;
	}
}
