style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", system-ui, -apple-system, sans-serif;
}

:root {
	--primary: #6366f1;
	--secondary: #4f46e5;
	--accent: #ec4899;
	--dark: #111827;
	--light: #f9fafb;
}

body {
	background: var(--light);
	overflow-x: hidden;
}

.hero {
	min-height: 100vh;
	padding: 120px 5% 60px;
	position: relative;
	overflow: hidden;
}

.gradient-background {
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	background: linear-gradient(45deg, var(--primary), var(--secondary));
	opacity: 0.1;
	z-index: -1;
}

.hero-content {
	max-width: 1200px;
	margin: 0 auto;
	display: grid;
	grid-template-columns: 1fr 1fr;
	gap: 4rem;
	align-items: center;
}

.hero-text {
	position: relative;
	z-index: 1;
}

.hero-title {
	font-size: clamp(2.5rem, 5vw, 3.5rem);
	line-height: 1.1;
	color: var(--dark);
	margin-bottom: 1.5rem;
	animation: slideInLeft 1s ease-out;
}

.hero-description {
	color: #6b7280;
	line-height: 1.6;
	margin-bottom: 2rem;
	max-width: 500px;
}

.cta-group {
	display: flex;
	gap: 1rem;
	margin-bottom: 3rem;
}

.cta-button {
	padding: 1rem 2rem;
	border-radius: 0.5rem;
	text-decoration: none;
	font-weight: 600;
	transition: transform 0.3s ease;
}

.primary-cta {
	background: var(--primary);
	color: white;
}

.secondary-cta {
	border: 2px solid var(--primary);
	color: var(--primary);
}

.feature-cards {
	display: grid;
	grid-template-columns: repeat(2, 1fr);
	gap: 1.5rem;
}

.feature-card {
	padding: 1.5rem;
	background: white;
	border-radius: 1rem;
	box-shadow: 0 10px 30px rgba(0, 0, 0, 0.05);
	transition: transform 0.3s ease;
}

.feature-card:hover {
	transform: translateY(-5px);
}

.card-icon {
	width: 40px;
	height: 40px;
	background: var(--primary);
	border-radius: 0.75rem;
	display: grid;
	place-items: center;
	margin-bottom: 1rem;
	color: white;
}

.hero-visual {
	position: relative;
	height: 500px;
	background: url("https://images.unsplash.com/photo-1552664730-d307ca884978?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80")
		center/cover;
	border-radius: 1rem;
	box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
	animation: float 6s ease-in-out infinite;
}

@keyframes slideInLeft {
	from {
		opacity: 0;
		transform: translateX(-50px);
	}
	to {
		opacity: 1;
		transform: translateX(0);
	}
}

@keyframes float {
	0%,
	100% {
		transform: translateY(0);
	}
	50% {
		transform: translateY(-20px);
	}
}

@media (max-width: 1024px) {
	.hero-content {
		grid-template-columns: 1fr;
		text-align: center;
	}

	.hero-description {
		margin: 0 auto 2rem;
	}

	.cta-group {
		justify-content: center;
	}

	.hero-visual {
		order: -1;
		height: 400px;
	}
}

@media (max-width: 768px) {
	.cta-group {
		flex-direction: column;
	}

	.feature-cards {
		grid-template-columns: 1fr;
	}

	.hero-visual {
		height: 300px;
	}
}
