style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: 'Arial', sans-serif;
}

.hero {
	height: 100vh;
	background: linear-gradient(rgba(0, 0, 0, 0.3), rgba(0, 0, 0, 0.3)),
		url('https://picsum.photos/1920/1080?blur=2');
	background-size: cover;
	background-position: center;
	display: flex;
	align-items: center;
	padding: 0 5%;
}

.hero-container {
	width: 100%;
	max-width: 1200px;
	margin: 0 auto;
	display: grid;
	grid-template-columns: 1fr 1fr;
	gap: 40px;
	align-items: center;
}

.hero-content {
	color: #fff;
}

.hero-content h1 {
	font-size: 3.5rem;
	margin-bottom: 20px;
}

.hero-content p {
	font-size: 1.2rem;
	margin-bottom: 30px;
}

.booking-form {
	background: rgba(255, 255, 255, 0.9);
	padding: 40px;
	border-radius: 15px;
	box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
}

.form-title {
	font-size: 2rem;
	margin-bottom: 30px;
	color: #333;
}

.form-group {
	margin-bottom: 20px;
}

.form-group label {
	display: block;
	margin-bottom: 8px;
	color: #666;
	font-weight: 500;
}

.form-input {
	width: 100%;
	padding: 12px 15px;
	border: 2px solid #ddd;
	border-radius: 8px;
	font-size: 1rem;
	transition: border-color 0.3s ease;
}

.form-input:focus {
	outline: none;
	border-color: #4caf50;
}

.submit-btn {
	width: 100%;
	padding: 15px;
	background: #4caf50;
	color: white;
	border: none;
	border-radius: 8px;
	font-size: 1.1rem;
	cursor: pointer;
	transition: background 0.3s ease;
}

.submit-btn:hover {
	background: #45a049;
}

@media (max-width: 992px) {
	.hero-container {
		grid-template-columns: 1fr;
		text-align: center;
	}

	.hero-content h1 {
		font-size: 2.5rem;
	}

	.booking-form {
		max-width: 500px;
		margin: 0 auto;
	}
}

@media (max-width: 576px) {
	.hero-content h1 {
		font-size: 2rem;
	}

	.booking-form {
		padding: 25px;
	}

	.form-title {
		font-size: 1.5rem;
	}
}
