style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", system-ui, -apple-system, sans-serif;
}

:root {
	--primary: #3b82f6;
	--secondary: #1d4ed8;
	--dark: #111827;
	--light: #f9fafb;
}

body {
	background: var(--light);
	overflow-x: hidden;
}

.hero {
	min-height: 100vh;
	padding: 120px 5% 60px;
	display: flex;
	align-items: center;
	position: relative;
}

.hero-content {
	max-width: 1200px;
	margin: 0 auto;
	display: grid;
	grid-template-columns: 1fr 1fr;
	gap: 4rem;
	align-items: center;
}

.hero-text {
	position: relative;
	z-index: 1;
}

.hero-title {
	font-size: clamp(2.5rem, 5vw, 3.5rem);
	line-height: 1.1;
	color: var(--dark);
	margin-bottom: 1.5rem;
	animation: slideInLeft 1s ease-out;
}

.hero-text p {
	color: #6b7280;
	line-height: 1.6;
	margin-bottom: 2rem;
	max-width: 500px;
	animation: fadeIn 1s ease-out 0.2s backwards;
}

.cta-button {
	display: inline-flex;
	align-items: center;
	padding: 1rem 2.5rem;
	background: var(--primary);
	color: white;
	border: none;
	border-radius: 0.5rem;
	font-weight: 600;
	text-decoration: none;
	transition: transform 0.3s ease;
	animation: fadeIn 1s ease-out 0.4s backwards;
}

.cta-button:hover {
	transform: translateY(-3px);
}

.cards-grid {
	display: grid;
	grid-template-columns: repeat(2, 1fr);
	gap: 2rem;
	position: relative;
}

.card {
	background: white;
	padding: 2rem;
	border-radius: 1rem;
	box-shadow: 0 10px 30px rgba(0, 0, 0, 0.05);
	transition: transform 0.3s ease;
}

.card:hover {
	transform: translateY(-10px);
}

.card-icon {
	width: 50px;
	height: 50px;
	background: var(--primary);
	border-radius: 0.75rem;
	display: grid;
	place-items: center;
	margin-bottom: 1rem;
	color: white;
}

.card h3 {
	color: var(--dark);
	margin-bottom: 0.5rem;
}

.card p {
	color: #6b7280;
	line-height: 1.6;
}

.decorative-shape {
	position: absolute;
	width: 200px;
	height: 200px;
	background: rgba(59, 130, 246, 0.1);
	border-radius: 40px;
	transform: rotate(45deg);
	z-index: -1;
	top: -50px;
	left: -50px;
}

@keyframes slideInLeft {
	from {
		opacity: 0;
		transform: translateX(-50px);
	}
	to {
		opacity: 1;
		transform: translateX(0);
	}
}

@keyframes fadeIn {
	from {
		opacity: 0;
	}
	to {
		opacity: 1;
	}
}

@media (max-width: 1024px) {
	.hero-content {
		grid-template-columns: 1fr;
		text-align: center;
	}

	.hero-text p {
		margin: 0 auto 2rem;
	}

	.cards-grid {
		order: -1;
		max-width: 600px;
		margin: 0 auto;
	}
}

@media (max-width: 768px) {
	.cards-grid {
		grid-template-columns: 1fr;
	}

	.decorative-shape {
		display: none;
	}
}
