style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: 'Inter', system-ui, -apple-system, sans-serif;
}

body {
	background-color: #f9f9f9;
}

.container {
	max-width: 1200px;
	margin: 0 auto;
	padding: 0 20px;
}

.logo {
	font-size: 1.5rem;
	font-weight: 700;
	color: #2d3436;
	text-decoration: none;
}

.hero-section {
	min-height: 90vh;
	display: grid;
	grid-template-columns: 1fr 1fr;
	align-items: center;
	gap: 4rem;
	padding: 4rem 0;
}

.hero-content {
	position: relative;
}

.hero-image {
	height: 500px;
	border-radius: 20px;
	background-size: cover;
	background-position: center;
	background-image: url('https://picsum.photos/800/600');
	box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
}

.hero-content h1 {
	font-size: 3.5rem;
	line-height: 1.1;
	color: #2d3436;
	margin-bottom: 1.5rem;
}

.hero-content p {
	font-size: 1.1rem;
	color: #636e72;
	line-height: 1.6;
	margin-bottom: 2rem;
}

.cta-button {
	display: inline-flex;
	align-items: center;
	margin-top: 1rem;
	padding: 1rem 2rem;
	background: linear-gradient(45deg, #6c5ce7, #a66efa);
	color: white;
	border: none;
	border-radius: 12px;
	font-weight: 600;
	cursor: pointer;
	transition: transform 0.3s ease;
	text-decoration: none;
}

.cta-button:hover {
	transform: translateY(-3px);
}

@media (max-width: 1024px) {
	.hero-section {
		grid-template-columns: 1fr;
		text-align: center;
		padding: 4rem 0;
	}

	.hero-image {
		height: 400px;
		order: -1;
	}

	.hero-content h1 {
		font-size: 2.5rem;
	}
}

@media (max-width: 768px) {
	.hero-content h1 {
		font-size: 2rem;
	}

	.hero-image {
		height: 300px;
	}

	.cta-button {
		padding: 0.8rem 1.5rem;
	}
}

.decorative-shape {
	position: absolute;
	width: 200px;
	height: 200px;
	background: rgba(108, 92, 231, 0.1);
	border-radius: 40px;
	transform: rotate(45deg);
	z-index: -1;
	top: -50px;
	left: -50px;
}

.stats {
	display: flex;
	gap: 2rem;
	margin-top: 3rem;
}

.stat-item {
	text-align: center;
}

.stat-number {
	font-size: 2rem;
	font-weight: 700;
	color: #6c5ce7;
}

.stat-label {
	color: #636e72;
	font-size: 0.9rem;
}
