style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: 'Inter', system-ui, -apple-system, sans-serif;
}

body {
	background: #f8f9fa;
}

.hero {
	min-height: 100vh;
	display: flex;
	align-items: center;
	padding: 100px 5% 60px;
	background: linear-gradient(45deg, rgba(43, 45, 66, 0.9), rgba(43, 45, 66, 0.8)),
		url('https://picsum.photos/1920/1080') center/cover;
}

.hero-content {
	max-width: 1200px;
	margin: 0 auto;
	color: white;
	width: 100%;
}

.hero-grid {
	display: grid;
	grid-template-columns: 1fr 1fr;
	gap: 4rem;
	align-items: center;
}

.hero-text {
	animation: slideIn 1s ease-out;
}

.hero-text h1 {
	font-size: clamp(2.5rem, 5vw, 4rem);
	line-height: 1.1;
	margin-bottom: 1.5rem;
}

.hero-text p {
	font-size: 1.1rem;
	line-height: 1.6;
	margin-bottom: 2rem;
	opacity: 0.9;
}

.cta-group {
	display: flex;
	gap: 1rem;
	margin-top: 2rem;
}

.btn {
	padding: 1rem 2rem;
	border-radius: 0.5rem;
	text-decoration: none;
	font-weight: 600;
	transition: all 0.3s ease;
}

.btn-primary {
	background: #ef233c;
	color: white;
}

.btn-primary:hover {
	background: #d90429;
	transform: translateY(-2px);
}

.btn-secondary {
	border: 2px solid white;
	color: white;
}

.btn-secondary:hover {
	background: white;
	color: #2b2d42;
}

.hero-highlights {
	display: grid;
	gap: 1.5rem;
	background: rgba(255, 255, 255, 0.1);
	padding: 2rem;
	border-radius: 1rem;
	backdrop-filter: blur(10px);
}

.highlight-item {
	display: flex;
	align-items: center;
	gap: 1rem;
}

.highlight-icon {
	width: 40px;
	height: 40px;
	background: #ef233c;
	border-radius: 0.5rem;
	display: grid;
	place-items: center;
}

@keyframes slideIn {
	from {
		opacity: 0;
		transform: translateY(30px);
	}
	to {
		opacity: 1;
		transform: translateY(0);
	}
}

@media (max-width: 1024px) {
	.hero-grid {
		grid-template-columns: 1fr;
		text-align: center;
		gap: 3rem;
	}

	.cta-group {
		justify-content: center;
	}

	.hero-highlights {
		max-width: 600px;
		margin: 0 auto;
	}
}

@media (max-width: 768px) {
	.hero-text h1 {
		font-size: 2.5rem;
	}

	.btn {
		padding: 0.8rem 1.5rem;
		font-size: 0.95rem;
	}

	.highlight-item {
		flex-direction: column;
		text-align: center;
	}
}
