style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: 'Inter', system-ui, -apple-system, sans-serif;
}

body {
	background: #0f172a;
}

.hero {
	min-height: 100vh;
	display: flex;
	align-items: center;
	padding: 120px 5% 60px;
	position: relative;
	overflow: hidden;
}

.hero-content {
	max-width: 1200px;
	margin: 0 auto;
	position: relative;
	z-index: 1;
	color: white;
}

.hero-grid {
	display: grid;
	grid-template-columns: 1fr 1fr;
	gap: 4rem;
	align-items: center;
}

.hero-text h1 {
	font-size: clamp(2.5rem, 5vw, 4rem);
	line-height: 1.1;
	margin-bottom: 1.5rem;
	background: linear-gradient(45deg, #7dd3fc, #38bdf8);
	-webkit-background-clip: text;
	background-clip: text;
	-webkit-text-fill-color: transparent;
}

.hero-text p {
	font-size: 1.1rem;
	line-height: 1.6;
	color: #cbd5e1;
	margin-bottom: 2rem;
}

.cta-button {
	display: inline-flex;
	align-items: center;
	padding: 1rem 2rem;
	background: linear-gradient(45deg, #38bdf8, #7dd3fc);
	color: white;
	border: none;
	border-radius: 0.5rem;
	font-weight: 600;
	text-decoration: none;
	transition: transform 0.3s ease;
}

.cta-button:hover {
	transform: translateY(-3px);
}

.hero-image {
	position: relative;
	border-radius: 1rem;
	overflow: hidden;
	aspect-ratio: 1/1;
	background: url('https://picsum.photos/800/800') center/cover;
	box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
}

.stats {
	position: absolute;
	bottom: -1px;
	left: -1px;
	background: rgba(15, 23, 42, 0.9);
	padding: 1.5rem;
	border-radius: 0 1rem 0 0;
	backdrop-filter: blur(10px);
	z-index: 999;
}

.stat-item {
	display: flex;
	align-items: center;
	gap: 1rem;
}

.stat-number {
	font-size: 1.5rem;
	font-weight: 700;
	color: #38bdf8;
}
