style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: 'Inter', system-ui, -apple-system, sans-serif;
}

:root {
	--primary: #6366f1;
	--secondary: #4f46e5;
	--accent: #ec4899;
	--dark: #1e293b;
	--light: #f8fafc;
}

.hero {
	min-height: 100vh;
	display: flex;
	align-items: center;
	padding: 120px 5% 60px;
	position: relative;
}

.hero-content {
	max-width: 1200px;
	margin: 0 auto;
	display: grid;
	grid-template-columns: 1fr 1fr;
	gap: 4rem;
	align-items: center;
}

.hero-title {
	font-size: clamp(2.5rem, 5vw, 4rem);
	line-height: 1.1;
	margin-bottom: 1.5rem;
	color: var(--dark);
	animation: slideIn 1s ease-out;
}

.hero-title span {
	color: var(--primary);
	position: relative;
}

.hero-title span::after {
	content: '';
	position: absolute;
	bottom: -10px;
	left: 0;
	width: 60%;
	height: 4px;
	background: var(--accent);
}

.hero-description {
	font-size: 1.1rem;
	line-height: 1.6;
	color: #64748b;
	margin-bottom: 2rem;
	max-width: 500px;
}

.cta-button {
	display: inline-flex;
	align-items: center;
	padding: 1rem 2.5rem;
	background: linear-gradient(45deg, var(--primary), var(--secondary));
	color: white;
	border: none;
	border-radius: 0.5rem;
	font-weight: 600;
	text-decoration: none;
	transition: transform 0.3s ease;
	position: relative;
	overflow: hidden;
}

.cta-button:hover {
	transform: translateY(-3px);
}

.hero-image {
	position: relative;
	border-radius: 1rem;
	overflow: hidden;
	aspect-ratio: 1/1;
	background: url('https://picsum.photos/800/800') center/cover;
	transform: rotate(3deg);
	animation: float 6s ease-in-out infinite;
}

@keyframes slideIn {
	from {
		opacity: 0;
		transform: translateX(-50px);
	}
	to {
		opacity: 1;
		transform: translateX(0);
	}
}

@keyframes float {
	0%, 100% {
		transform: translateY(0) rotate(3deg);
	}
	50% {
		transform: translateY(-20px) rotate(-3deg);
	}
}
