style:
:root {
	--bg: #ffffff;
	--text: #2d3436;
	--primary: #6c5ce7;
	--secondary: #00b894;
	--loader-size: 40px;
	--animation-speed: 1s;
}

[data-theme="dark"] {
	--bg: #2d3436;
	--text: #dfe6e9;
	--primary: #a66efa;
	--secondary: #55efc4;
}

body {
	margin: 0;
	padding: 2rem;
	min-height: 100vh;
	background: var(--bg);
	color: var(--text);
	transition: all 0.3s ease;
	display: flex;
	flex-direction: column;
	align-items: center;
	font-family: system-ui, sans-serif;
}

.theme-toggle {
	position: fixed;
	top: 1rem;
	right: 1rem;
	padding: 0.75rem 1.5rem;
	background: linear-gradient(45deg, var(--primary), var(--secondary));
	color: var(--bg);
	border: none;
	border-radius: 8px;
	cursor: pointer;
	font-weight: 600;
	transition: transform 0.2s;
}

.theme-toggle:hover {
	transform: translateY(-2px);
}

/* Circle Dash Loader Styles */
.circle-dash {
	width: var(--loader-size);
	height: var(--loader-size);
	border: 3px solid transparent;
	border-top-color: var(--primary);
	border-bottom-color: var(--secondary);
	border-radius: 50%;
	animation: spin var(--animation-speed) linear infinite;
	position: relative;
}

.circle-dash::after {
	content: "";
	position: absolute;
	top: -3px;
	left: -3px;
	right: -3px;
	bottom: -3px;
	border: 3px dashed var(--primary);
	border-radius: 50%;
	animation: dash calc(var(--animation-speed) * 2) linear infinite;
}

@keyframes spin {
	100% {
		transform: rotate(360deg);
	}
}

@keyframes dash {
	0% {
		transform: rotate(0deg);
		stroke-dashoffset: 0;
	}
	100% {
		transform: rotate(360deg);
		stroke-dashoffset: -100;
	}
}

script:
function toggleTheme() {
	const body = document.body;
	const currentTheme = body.getAttribute("data-theme") || "light";
	const newTheme = currentTheme === "dark" ? "light" : "dark";
	body.setAttribute("data-theme", newTheme);
	localStorage.setItem("loader-theme-v3", newTheme);
}

const savedTheme = localStorage.getItem("loader-theme-v3");
if (savedTheme) {
	document.body.setAttribute("data-theme", savedTheme);
}
