style:
:root {
    --bg: #ffffff;
    --text: #2d3436;
    --primary: #6c5ce7;
    --secondary: #00b894;
    --loader-size: 40px;
    --animation-speed: 1s;
}
[data-theme="dark"] {
    --bg: #2d3436;
    --text: #dfe6e9;
    --primary: #a66efa;
    --secondary: #55efc4;
}
body {
    margin: 0;
    padding: 2rem;
    min-height: 100vh;
    background: var(--bg);
    color: var(--text);
    transition: all 0.3s ease;
    display: flex;
    flex-direction: column;
    align-items: center;
    font-family: system-ui, sans-serif;
}
.theme-toggle {
    position: fixed;
    top: 1rem;
    right: 1rem;
    padding: 0.75rem 1.5rem;
    background: linear-gradient(45deg, var(--primary), var(--secondary));
    color: var(--bg);
    border: none;
    border-radius: 8px;
    cursor: pointer;
    font-weight: 600;
    transition: transform 0.2s;
}
.theme-toggle:hover {
    transform: translateY(-2px);
}
/* Folding Cube Loader Styles */
.folding-cube {
    width: var(--loader-size);
    height: var(--loader-size);
    position: relative;
    transform: rotateZ(45deg);
}
.folding-cube div {
    position: absolute;
    width: 50%;
    height: 50%;
    background: var(--primary);
    animation: fold var(--animation-speed) infinite linear;
    transform-style: preserve-3d;
}
.folding-cube div:nth-child(1) {
    top: 0;
    left: 0;
    animation-delay: calc(var(--animation-speed) * -0.5);
}
.folding-cube div:nth-child(2) {
    top: 0;
    right: 0;
    background: var(--secondary);
    animation-delay: calc(var(--animation-speed) * -0.375);
}
.folding-cube div:nth-child(3) {
    bottom: 0;
    right: 0;
    animation-delay: calc(var(--animation-speed) * -0.25);
}
.folding-cube div:nth-child(4) {
    bottom: 0;
    left: 0;
    background: var(--secondary);
    animation-delay: calc(var(--animation-speed) * -0.125);
}
@keyframes fold {
    0%,
    10% {
        transform: perspective(120px) rotateX(-180deg);
        opacity: 0;
    }
    25%,
    75% {
        transform: perspective(120px) rotateX(0deg);
        opacity: 1;
    }
    90%,
    100% {
        transform: perspective(120px) rotateY(180deg);
        opacity: 0;
    }
}

script:
function toggleTheme() {
    const body = document.body;
    const currentTheme = body.getAttribute("data-theme") || "light";
    const newTheme = currentTheme === "dark" ? "light" : "dark";
    body.setAttribute("data-theme", newTheme);
    localStorage.setItem("loader-theme-v3", newTheme);
}
const savedTheme = localStorage.getItem("loader-theme-v3");
if (savedTheme) {
    document.body.setAttribute("data-theme", savedTheme);
}
