style:
:root {
    --bg: #f0f2f5;
    --text: #1a1a1a;
    --primary: #2563eb;
    --secondary: #7c3aed;
    --neon-glow: 0 0 10px rgba(37, 99, 235, 0.5);
    --loader-speed: 1.4s;
}
[data-theme="dark"] {
    --bg: #0f172a;
    --text: #e2e8f0;
    --primary: #818cf8;
    --secondary: #c084fc;
    --neon-glow: 0 0 15px rgba(129, 140, 248, 0.7);
}
body {
    margin: 0;
    padding: 2rem;
    min-height: 50vh;
    background: var(--bg);
    color: var(--text);
    transition: all 0.4s ease;
    display: grid;
    place-items: center;
    font-family: "Inter", system-ui, sans-serif;
}
.theme-toggle {
    position: fixed;
    top: 1rem;
    right: 1rem;
    padding: 0.8rem 1.6rem;
    background: linear-gradient(135deg, var(--primary), var(--secondary));
    color: var(--bg);
    border: none;
    border-radius: 8px;
    cursor: pointer;
    font-weight: 600;
    transition: transform 0.2s, box-shadow 0.2s;
    box-shadow: var(--neon-glow);
}
.theme-toggle:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
}
/* Morphing Blob */
.morphing-blob {
    width: 60px;
    height: 60px;
    background: var(--primary);
    border-radius: 50%;
    animation: morph calc(var(--loader-speed) * 2) ease-in-out infinite;
    position: relative;
    filter: blur(1px);
}
.morphing-blob::after {
    content: "";
    position: absolute;
    inset: 0;
    background: var(--secondary);
    border-radius: 50%;
    animation: pulse calc(var(--loader-speed) * 2) ease-in-out infinite;
}
@keyframes morph {
    0%,
    100% {
        border-radius: 50%;
        transform: scale(1);
    }
    50% {
        border-radius: 30%;
        transform: scale(0.8);
    }
}
@keyframes pulse {
    0%,
    100% {
        opacity: 1;
        transform: scale(1);
    }
    50% {
        opacity: 0.5;
        transform: scale(0.8);
    }
}

script:
function toggleTheme() {
    const body = document.body;
    const isDark = body.getAttribute("data-theme") === "dark";
    body.setAttribute("data-theme", isDark ? "" : "dark");
    localStorage.setItem("neon-theme", isDark ? "light" : "dark");
    document.querySelector(".theme-toggle").textContent = isDark ? "Neon Theme" : "Dark Theme";
}
const savedTheme = localStorage.getItem("neon-theme");
if (savedTheme === "dark") {
    document.body.setAttribute("data-theme", "dark");
    document.querySelector(".theme-toggle").textContent = "Dark Theme";
}
