style:
:root {
    --bg: #f0f2f5;
    --text: #1a1a1a;
    --primary: #2563eb;
    --secondary: #7c3aed;
    --neon-glow: 0 0 10px rgba(37, 99, 235, 0.5);
    --loader-speed: 1.4s;
}
[data-theme="dark"] {
    --bg: #0f172a;
    --text: #e2e8f0;
    --primary: #818cf8;
    --secondary: #c084fc;
    --neon-glow: 0 0 15px rgba(129, 140, 248, 0.7);
}
body {
    margin: 0;
    padding: 2rem;
    min-height: 50vh;
    background: var(--bg);
    color: var(--text);
    transition: all 0.4s ease;
    display: grid;
    place-items: center;
    font-family: "Inter", system-ui, sans-serif;
}
.theme-toggle {
    position: fixed;
    top: 1rem;
    right: 1rem;
    padding: 0.8rem 1.6rem;
    background: linear-gradient(135deg, var(--primary), var(--secondary));
    color: var(--bg);
    border: none;
    border-radius: 8px;
    cursor: pointer;
    font-weight: 600;
    transition: transform 0.2s, box-shadow 0.2s;
    box-shadow: var(--neon-glow);
}
.theme-toggle:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
}
/* Ripple Loader */
.ripple-loader {
    width: 60px;
    height: 60px;
    position: relative;
}
.ripple-loader div {
    position: absolute;
    border: 3px solid var(--primary);
    border-radius: 50%;
    animation: ripple var(--loader-speed) cubic-bezier(0, 0.2, 0.8, 1) infinite;
}
.ripple-loader div:nth-child(2) {
    animation-delay: -0.5s;
    border-color: var(--secondary);
}
@keyframes ripple {
    0% {
        width: 0;
        height: 0;
        opacity: 1;
    }
    100% {
        width: 100%;
        height: 100%;
        opacity: 0;
    }
}

script:
function toggleTheme() {
    const body = document.body;
    const isDark = body.getAttribute("data-theme") === "dark";
    body.setAttribute("data-theme", isDark ? "" : "dark");
    localStorage.setItem("neon-theme", isDark ? "light" : "dark");
    document.querySelector(".theme-toggle").textContent = isDark ? "Neon Theme" : "Dark Theme";
}
const savedTheme = localStorage.getItem("neon-theme");
if (savedTheme === "dark") {
    document.body.setAttribute("data-theme", "dark");
    document.querySelector(".theme-toggle").textContent = "Dark Theme";
}
