style:
:root {
	--bg: #ffffff;
	--text: #2d3436;
	--primary: #6c5ce7;
	--secondary: #a8a8a8;
	--transition: all 0.4s ease-in-out;
}

[data-theme="dark"] {
	--bg: #2d3436;
	--text: #ffffff;
	--primary: #a66efa;
	--secondary: #636e72;
}

* {
	box-sizing: border-box;
	margin: 0;
	padding: 0;
	transition: var(--transition);
}

body {
	min-height: 100vh;
	display: grid;
	place-items: center;
	background: var(--bg);
	color: var(--text);
	font-family: "Inter", system-ui, sans-serif;
	padding: 2rem;
}

.theme-switcher {
	position: fixed;
	top: 1.5rem;
	right: 1.5rem;
	padding: 0.8rem;
	border: none;
	border-radius: 12px;
	background: var(--primary);
	color: var(--bg);
	cursor: pointer;
	display: flex;
	align-items: center;
	gap: 0.5rem;
}

.progress-spinner {
	width: 80px;
	height: 80px;
	border-radius: 50%;
	background: conic-gradient(var(--primary) calc(var(--progress) * 1%), transparent 0);
	-webkit-mask: radial-gradient(farthest-side, transparent calc(98% - 8px), black calc(100% - 8px));
	mask: radial-gradient(farthest-side, transparent calc(98% - 8px), black calc(100% - 8px));
	animation: rotate 2s linear infinite;
}

@keyframes rotate {
	100% {
		transform: rotate(360deg);
	}
}

script:
const themeSwitcher = document.querySelector(".theme-switcher");
const body = document.body;
const savedTheme = localStorage.getItem("theme") || "light";
body.setAttribute("data-theme", savedTheme);
themeSwitcher.textContent = savedTheme === "dark" ? "ðŸŒœ" : "ðŸŒž";
themeSwitcher.addEventListener("click", () => {
	const newTheme = body.getAttribute("data-theme") === "dark" ? "light" : "dark";
	body.setAttribute("data-theme", newTheme);
	localStorage.setItem("theme", newTheme);
	themeSwitcher.textContent = newTheme === "dark" ? "ðŸŒœ" : "ðŸŒž";
});
