style:
:root {
	--bg: #ffffff;
	--text: #2d3436;
	--primary: #6c5ce7;
	--secondary: #a8a8a8;
	--transition: all 0.4s ease-in-out;
}

[data-theme="dark"] {
	--bg: #2d3436;
	--text: #ffffff;
	--primary: #a66efa;
	--secondary: #636e72;
}

* {
	box-sizing: border-box;
	margin: 0;
	padding: 0;
	transition: var(--transition);
}

body {
	min-height: 100vh;
	display: grid;
	place-items: center;
	background: var(--bg);
	color: var(--text);
	font-family: "Inter", system-ui, sans-serif;
	padding: 2rem;
}

.theme-switcher {
	position: fixed;
	top: 1.5rem;
	right: 1.5rem;
	padding: 0.8rem;
	border: none;
	border-radius: 12px;
	background: var(--primary);
	color: var(--bg);
	cursor: pointer;
	display: flex;
	align-items: center;
	gap: 0.5rem;
}

.circle-fade {
	position: relative;
	width: 80px;
	height: 80px;
}

.circle-fade div {
	position: absolute;
	width: 100%;
	height: 100%;
	animation: circleFade 1.2s linear infinite;
}

.circle-fade div:after {
	content: "";
	display: block;
	margin: 4px;
	width: 12px;
	height: 12px;
	background: var(--primary);
	border-radius: 50%;
}

.circle-fade div:nth-child(1) {
	transform: rotate(0deg);
	animation-delay: -1.1s;
}

.circle-fade div:nth-child(2) {
	transform: rotate(30deg);
	animation-delay: -1s;
}

.circle-fade div:nth-child(3) {
	transform: rotate(60deg);
	animation-delay: -0.9s;
}

.circle-fade div:nth-child(4) {
	transform: rotate(90deg);
	animation-delay: -0.8s;
}

.circle-fade div:nth-child(5) {
	transform: rotate(120deg);
	animation-delay: -0.7s;
}

.circle-fade div:nth-child(6) {
	transform: rotate(150deg);
	animation-delay: -0.6s;
}

@keyframes circleFade {
	0% {
		opacity: 1;
	}
	100% {
		opacity: 0;
	}
}

script:
const themeSwitcher = document.querySelector(".theme-switcher");
const body = document.body;
const savedTheme = localStorage.getItem("theme") || "light";
body.setAttribute("data-theme", savedTheme);
themeSwitcher.textContent = savedTheme === "dark" ? "ðŸŒœ" : "ðŸŒž";
themeSwitcher.addEventListener("click", () => {
	const newTheme = body.getAttribute("data-theme") === "dark" ? "light" : "dark";
	body.setAttribute("data-theme", newTheme);
	localStorage.setItem("theme", newTheme);
	themeSwitcher.textContent = newTheme === "dark" ? "ðŸŒœ" : "ðŸŒž";
});
