style:
:root {
    --bg: #fafafa;
    --text: #2d3436;
    --primary: #3b82f6;
    --secondary: #8b5cf6;
    --hologlow: 0 0 15px rgba(59, 130, 246, 0.2);
    --loader-speed: 1.6s;
}
[data-theme="dark"] {
    --bg: #0f172a;
    --text: #e2e8f0;
    --primary: #818cf8;
    --secondary: #c084fc;
    --hologlow: 0 0 25px rgba(129, 140, 248, 0.3);
}
body {
    margin: 0;
    padding: 2rem;
    min-height: 50vh;
    background: var(--bg);
    color: var(--text);
    transition: all 0.4s ease;
    display: grid;
    place-items: center;
    font-family: "Inter", system-ui, sans-serif;
}
.theme-switch {
    position: fixed;
    top: 1rem;
    right: 1rem;
    width: 50px;
    height: 26px;
    background: var(--text);
    border-radius: 13px;
    cursor: pointer;
    transition: background 0.3s;
}
.theme-switch::before {
    content: "";
    position: absolute;
    width: 20px;
    height: 20px;
    background: var(--bg);
    border-radius: 50%;
    top: 3px;
    left: 3px;
    transition: transform 0.3s;
}
[data-theme="dark"] .theme-switch::before {
    transform: translateX(24px);
}
/* Phase Rings */
.phase-rings {
    width: 60px;
    height: 60px;
    position: relative;
}
.phase-rings div {
    position: absolute;
    width: 100%;
    height: 100%;
    border: 2px solid transparent;
    border-radius: 50%;
    animation: phase calc(var(--loader-speed) * 2) cubic-bezier(0.4, 0, 0.2, 1) infinite;
}
.phase-rings div:nth-child(1) {
    border-top-color: var(--primary);
    animation-delay: -0.5s;
}
.phase-rings div:nth-child(2) {
    border-right-color: var(--secondary);
    animation-delay: -1s;
}
@keyframes phase {
    0% {
        transform: rotate(0deg) scale(1);
        opacity: 1;
    }
    50% {
        transform: rotate(180deg) scale(0.8);
        opacity: 0.5;
    }
    100% {
        transform: rotate(360deg) scale(1);
        opacity: 1;
    }
}

script:
function toggleTheme() {
    const body = document.body;
    const isDark = body.getAttribute("data-theme") === "dark";
    body.setAttribute("data-theme", isDark ? "" : "dark");
    localStorage.setItem("holographic-theme", isDark ? "light" : "dark");
}
const savedTheme = localStorage.getItem("holographic-theme");
if (savedTheme === "dark") {
    document.body.setAttribute("data-theme", "dark");
}
