style:
:root {
    --bg: #f8fafc;
    --text: #1e293b;
    --primary: #3b82f6;
    --secondary: #f59e0b;
    --loader-speed: 1.2s;
    --size: 50px;
}
[data-theme="dark"] {
    --bg: #0f172a;
    --text: #f8fafc;
    --primary: #818cf8;
    --secondary: #fbbf24;
}
* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
    transition: all 0.3s ease;
}
body {
    margin: 0;
    padding: 2rem;
    min-height: 100vh;
    background: var(--bg);
    color: var(--text);
    display: grid;
    place-items: center;
    font-family: "Inter", system-ui;
}
.theme-toggle {
    position: fixed;
    top: 1rem;
    right: 1rem;
    width: 40px;
    height: 40px;
    background: var(--primary);
    border: none;
    border-radius: 50%;
    cursor: pointer;
    transition: transform 0.3s;
    display: grid;
    place-items: center;
}
.theme-toggle svg {
    width: 60%;
    height: 60%;
    fill: var(--bg);
    transition: transform 0.3s;
}
/* Hexagon Loader */
.hex-loader {
    width: var(--size);
    height: var(--size);
    position: relative;
    clip-path: polygon(50% 0%, 100% 25%, 100% 75%, 50% 100%, 0% 75%, 0% 25%);
    background: conic-gradient(var(--primary), var(--secondary), var(--primary));
    animation: rotate var(--loader-speed) linear infinite;
}
.hex-loader::after {
    content: "";
    position: absolute;
    inset: 2px;
    background: var(--bg);
    clip-path: polygon(50% 0%, 100% 25%, 100% 75%, 50% 100%, 0% 75%, 0% 25%);
}
@keyframes rotate {
    to {
        transform: rotate(360deg);
    }
}

script:
function toggleTheme() {
    const body = document.body;
    const isDark = body.getAttribute("data-theme") === "dark";
    body.setAttribute("data-theme", isDark ? "" : "dark");
    localStorage.setItem("geo-theme", isDark ? "light" : "dark");

    const toggle = document.querySelector(".theme-toggle");
    toggle.style.transform = "scale(0.9)";
    setTimeout(() => (toggle.style.transform = "scale(1)"), 100);
}
const savedTheme = localStorage.getItem("geo-theme");
if (savedTheme === "dark") {
    document.body.setAttribute("data-theme", "dark");
}
