style:
:root {
	--bg: #f8fafc;
	--text: #1e293b;
	--primary: #3b82f6;
	--secondary: #f59e0b;
	--loader-speed: 1.2s;
}

[data-theme="dark"] {
	--bg: #0f172a;
	--text: #f8fafc;
	--primary: #818cf8;
	--secondary: #fbbf24;
}

* {
	box-sizing: border-box;
	margin: 0;
	padding: 0;
	transition: all 0.3s ease;
}

body {
	margin: 0;
	padding: 2rem;
	min-height: 100vh;
	background: var(--bg);
	color: var(--text);
	display: grid;
	place-items: center;
	font-family: "Inter", system-ui;
}

.theme-toggle {
	position: fixed;
	top: 1rem;
	right: 1rem;
	width: 40px;
	height: 40px;
	background: var(--primary);
	border: none;
	border-radius: 50%;
	cursor: pointer;
	transition: transform 0.3s;
	display: grid;
	place-items: center;
}

.theme-toggle svg {
	width: 60%;
	height: 60%;
	fill: var(--bg);
	transition: transform 0.3s;
}

/* Pulsing Triangles Loader */
.tri-loader {
	display: flex;
	gap: 0.5rem;
}

.tri-loader div {
	width: 0;
	height: 0;
	border-left: 12px solid transparent;
	border-right: 12px solid transparent;
	border-bottom: 20px solid var(--primary);
	animation: pulse var(--loader-speed) ease-in-out infinite;
}

.tri-loader div:nth-child(2) {
	border-bottom-color: var(--secondary);
	animation-delay: 0.2s;
}

@keyframes pulse {
	0%,
	100% {
		transform: translateY(0);
	}
	50% {
		transform: translateY(-10px) scale(1.2);
	}
}

script:
function toggleTheme() {
	const body = document.body;
	const isDark = body.getAttribute("data-theme") === "dark";
	body.setAttribute("data-theme", isDark ? "" : "dark");
	localStorage.setItem("geo-theme", isDark ? "light" : "dark");
	const toggle = document.querySelector(".theme-toggle");
	toggle.style.transform = "scale(0.9)";
	setTimeout(() => (toggle.style.transform = "scale(1)"), 100);
}

const savedTheme = localStorage.getItem("geo-theme");
if (savedTheme === "dark") {
	document.body.setAttribute("data-theme", "dark");
}
