style:
:root {
	--bg: #f8fafc;
	--text: #1e293b;
	--primary: #3b82f6;
	--secondary: #f59e0b;
	--loader-speed: 1.2s;
	--size: 50px;
}
[data-theme="dark"] {
	--bg: #0f172a;
	--text: #f8fafc;
	--primary: #818cf8;
	--secondary: #fbbf24;
}
* {
	box-sizing: border-box;
	margin: 0;
	padding: 0;
	transition: all 0.3s ease;
}
body {
	margin: 0;
	padding: 2rem;
	min-height: 100vh;
	background: var(--bg);
	color: var(--text);
	display: grid;
	place-items: center;
	font-family: "Inter", system-ui;
}
.theme-toggle {
	position: fixed;
	top: 1rem;
	right: 1rem;
	width: 40px;
	height: 40px;
	background: var(--primary);
	border: none;
	border-radius: 50%;
	cursor: pointer;
	transition: transform 0.3s;
	display: grid;
	place-items: center;
}
.theme-toggle svg {
	width: 60%;
	height: 60%;
	fill: var(--bg);
	transition: transform 0.3s;
}
.orbital-loader {
	width: var(--size);
	height: var(--size);
	position: relative;
}
.orbital-loader div {
	width: 8px;
	height: 8px;
	background: var(--secondary);
	border-radius: 50%;
	position: absolute;
	animation: orbit calc(var(--loader-speed) * 2) linear infinite;
}
.orbital-loader div:nth-child(2) {
	animation-delay: -1s;
}
@keyframes orbit {
	0% {
		transform: rotate(0deg) translateX(20px) rotate(0deg);
	}
	100% {
		transform: rotate(360deg) translateX(20px) rotate(-360deg);
	}
}

script:
function toggleTheme() {
	const body = document.body;
	const isDark = body.getAttribute("data-theme") === "dark";
	body.setAttribute("data-theme", isDark ? "" : "dark");
	localStorage.setItem("geo-theme", isDark ? "light" : "dark");
	const toggle = document.querySelector(".theme-toggle");
	toggle.style.transform = "scale(0.9)";
	setTimeout(() => (toggle.style.transform = "scale(1)"), 100);
}
const savedTheme = localStorage.getItem("geo-theme");
if (savedTheme === "dark") {
	document.body.setAttribute("data-theme", "dark");
}
