style:
:root {
    --bg: #f0f2f5;
    --text: #2d3436;
    --primary: #4f46e5;
    --secondary: #ec4899;
    --shadow: 8px 8px 16px #d9dbe3, -8px -8px 16px #ffffff;
    --loader-speed: 3s;
}
[data-theme="dark"] {
    --bg: #1a1c22;
    --text: #e8e8e8;
    --primary: #818cf8;
    --secondary: #f472b6;
    --shadow: 8px 8px 16px #0f1013, -8px -8px 16px #252831;
}
* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
}
body {
    margin: 0;
    padding: 2rem;
    min-height: 100vh;
    background: var(--bg);
    color: var(--text);
    display: grid;
    place-items: center;
    font-family: "Inter", system-ui;
}
.theme-toggle {
    position: fixed;
    top: 1rem;
    right: 1rem;
    width: 48px;
    height: 48px;
    border-radius: 50%;
    background: var(--bg);
    box-shadow: var(--shadow);
    cursor: pointer;
    display: grid;
    place-items: center;
    transition: transform 0.3s;
}
.theme-toggle svg {
    width: 24px;
    height: 24px;
    fill: var(--text);
    transition: transform 0.3s;
}
/* 3D Cube Loader */
.cube-loader {
    width: 60px;
    height: 60px;
    position: relative;
    transform-style: preserve-3d;
    animation: cubeRotate var(--loader-speed) infinite ease-in-out;
}
.cube-face {
    position: absolute;
    width: 100%;
    height: 100%;
    background: var(--primary);
    border: 2px solid var(--bg);
    box-shadow: var(--shadow);
    transform-style: preserve-3d;
}
.front {
    transform: translateZ(30px);
}
.back {
    transform: translateZ(-30px) rotateY(180deg);
}
.right {
    transform: rotateY(90deg) translateZ(30px);
}
.left {
    transform: rotateY(-90deg) translateZ(30px);
}
.top {
    transform: rotateX(90deg) translateZ(30px);
}
.bottom {
    transform: rotateX(-90deg) translateZ(30px);
}
@keyframes cubeRotate {
    0% {
        transform: rotateX(0deg) rotateY(0deg);
    }
    100% {
        transform: rotateX(360deg) rotateY(720deg);
    }
}

script:
function toggleTheme() {
    const body = document.body;
    const isDark = body.getAttribute("data-theme") === "dark";
    body.setAttribute("data-theme", isDark ? "" : "dark");
    localStorage.setItem("neo-theme", isDark ? "light" : "dark");
    const toggle = document.querySelector(".theme-toggle");
    toggle.style.transform = "scale(0.9) rotate(180deg)";
    setTimeout(() => (toggle.style.transform = "scale(1) rotate(0deg)"), 300);
}
const savedTheme = localStorage.getItem("neo-theme");
if (savedTheme === "dark") {
    document.body.setAttribute("data-theme", "dark");
}
