style:
:root {
    --bg: #f0f2f5;
    --text: #2d3436;
    --primary: #4f46e5;
    --secondary: #ec4899;
    --shadow: 8px 8px 16px #d9dbe3, -8px -8px 16px #ffffff;
    --loader-speed: 4s;
}
[data-theme="dark"] {
    --bg: #1a1c22;
    --text: #e8e8e8;
    --primary: #818cf8;
    --secondary: #f472b6;
    --shadow: 8px 8px 16px #0f1013, -8px -8px 16px #252831;
}
* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
}
body {
    margin: 0;
    padding: 2rem;
    min-height: 100vh;
    background: var(--bg);
    color: var(--text);
    display: grid;
    place-items: center;
    font-family: "Inter", system-ui;
}
.theme-toggle {
    position: fixed;
    top: 1rem;
    right: 1rem;
    width: 48px;
    height: 48px;
    border-radius: 50%;
    background: var(--bg);
    box-shadow: var(--shadow);
    cursor: pointer;
    display: grid;
    place-items: center;
    transition: transform 0.3s;
}
.theme-toggle svg {
    width: 24px;
    height: 24px;
    fill: var(--text);
    transition: transform 0.3s;
}
/* Binary Loader */
.binary-loader {
    display: flex;
    gap: 4px;
    perspective: 1000px;
}
.binary-digit {
    width: 12px;
    height: 24px;
    background: var(--primary);
    animation: binaryFlip calc(var(--loader-speed) / 2) infinite ease-in-out;
    transform-style: preserve-3d;
    position: relative;
}
@keyframes binaryFlip {
    0%,
    45% {
        transform: rotateX(0deg);
    }
    50%,
    95% {
        transform: rotateX(-180deg);
    }
    100% {
        transform: rotateX(-360deg);
    }
}

script:
function toggleTheme() {
  const body = document.body;
  const isDark = body.getAttribute("data-theme") === "dark";
  body.setAttribute("data-theme", isDark ? "" : "dark");
  localStorage.setItem("neo-theme", isDark ? "light" : "dark");
  const toggle = document.querySelector(".theme-toggle");
  toggle.style.transform = "scale(0.9) rotate(180deg)";
  setTimeout(() => (toggle.style.transform = "scale(1) rotate(0deg)"), 300);
}
const savedTheme = localStorage.getItem("neo-theme");
if (savedTheme === "dark") {
  document.body.setAttribute("data-theme", "dark");
}
