style:
body {
	display: flex;
	justify-content: center;
	align-items: center;
	height: 100vh;
	margin: 0;
	background-color: #f0f0f0;
	transition: background-color 0.3s, color 0.3s;
}
.wave {
	width: 5px;
	height: 100px;
	background: #3498db;
	margin: 10px;
	animation: wave 1.2s infinite ease-in-out;
	display: inline-block;
}
.wave:nth-child(2) {
	animation-delay: -1.1s;
}
.wave:nth-child(3) {
	animation-delay: -1s;
}
.wave:nth-child(4) {
	animation-delay: -0.9s;
}
.wave:nth-child(5) {
	animation-delay: -0.8s;
}
@keyframes wave {
	0%,
	40%,
	100% {
		transform: scaleY(0.4);
	}
	20% {
		transform: scaleY(1);
	}
}
#theme-toggle {
	position: fixed;
	bottom: 20px;
	right: 20px;
	background: none;
	border: none;
	cursor: pointer;
	font-size: 20px;
}
.dark-mode {
	background-color: #2c2c2c;
	color: #fff;
}
.dark-mode .wave {
	background-color: #4caf50;
}
