style:
body {
	display: flex;
	justify-content: center;
	align-items: center;
	height: 100vh;
	margin: 0;
	background: #000;
	font-family: "Arial", sans-serif;
}
.neon-text {
	font-size: 4em;
	color: #3498db;
	text-shadow: 0 0 10px #3498db, 0 0 20px #3498db, 0 0 30px #3498db, 0 0 40px #2980b9, 0 0 70px #2980b9, 0 0 80px #2980b9, 0 0 100px #2980b9;
	animation: pulse 1.5s infinite alternate;
}
@keyframes pulse {
	from {
		text-shadow: 0 0 10px #3498db, 0 0 20px #3498db, 0 0 30px #3498db, 0 0 40px #2980b9, 0 0 70px #2980b9, 0 0 80px #2980b9, 0 0 100px #2980b9;
	}
	to {
		text-shadow: 0 0 5px #3498db, 0 0 10px #3498db, 0 0 15px #3498db, 0 0 20px #2980b9, 0 0 35px #2980b9, 0 0 40px #2980b9, 0 0 50px #2980b9;
	}
}
