style:
body {
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
    margin: 0;
    background: #000;
}
.neon-dots {
    display: flex;
}
.dot {
    width: 20px;
    height: 20px;
    background: #e74c3c; /* Neon red */
    border-radius: 50%;
    margin: 0 10px;
    animation: glow 1.5s ease-in-out infinite alternate;
}
.dot:nth-child(2) {
    animation-delay: 0.3s;
}
.dot:nth-child(3) {
    animation-delay: 0.6s;
}
@keyframes glow {
    from {
        box-shadow: 0 0 5px #e74c3c, 0 0 10px #e74c3c, 0 0 15px #e74c3c, 0 0 20px #c0392b, 0 0 30px #c0392b, 0 0 40px #c0392b;
    }
    to {
        box-shadow: 0 0 10px #e74c3c, 0 0 20px #e74c3c, 0 0 30px #e74c3c, 0 0 40px #c0392b, 0 0 60px #c0392b, 0 0 80px #c0392b;
    }
}
