style:
body {
	display: flex;
	justify-content: center;
	align-items: center;
	height: 100vh;
	margin: 0;
	background: #f0f0f0;
}
.loading-bar {
	width: 200px;
	height: 10px;
	background: #ddd;
	border-radius: 5px;
	overflow: hidden;
}
.progress {
	width: 0;
	height: 100%;
	background: #3498db;
	animation: load 3s linear infinite;
	box-shadow: 0 0 3px #3498db;
}
@keyframes load {
	0% {
		width: 0%;
	}
	50% {
		width: 100%;
	}
	100% {
		width: 0;
	}
}
