style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: 'Arial', sans-serif;
}

body {
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	background: linear-gradient(45deg, #ff6b6b, #142726);
}

.login-container {
	background: rgba(255, 255, 255, 0.95);
	padding: 40px;
	border-radius: 20px;
	box-shadow: 0 15px 35px rgba(0, 0, 0, 0.2);
	width: 400px;
}

.login-header {
	text-align: center;
	margin-bottom: 40px;
}

.login-header h1 {
	color: #333;
	font-size: 1.8em;
}

.input-group {
	margin-bottom: 20px;
	position: relative;
}

.input-group input {
	width: 100%;
	padding: 15px 40px;
	border: none;
	background: #f0f0f0;
	border-radius: 10px;
	font-size: 16px;
	transition: 0.3s;
}

.input-group input:focus {
	outline: none;
	background: #e0e0e0;
}

.input-group i {
	position: absolute;
	left: 15px;
	top: 50%;
	transform: translateY(-50%);
	color: #888;
}

.input-group i.show-password {
	right: 15px;
	left: auto;
	cursor: pointer;
	color: #888;
	width: 24px;
}

.show-password:hover {
	color: #333;
}

button {
	width: 100%;
	padding: 15px;
	background: #142726;
	color: white;
	border: none;
	border-radius: 10px;
	font-size: 16px;
	cursor: pointer;
	transition: 0.3s;
}

button:hover {
	background: #2a5553;
}

@media (max-width: 480px) {
	.login-container {
		width: 90%;
		padding: 30px;
	}
}

script:
function togglePassword() {
	const passwordInput = document.getElementById('password');
	const eyeIcon = document.querySelector('.show-password');

	if (passwordInput.type === 'password') {
		passwordInput.type = 'text';
		eyeIcon.classList.replace('fa-eye', 'fa-eye-slash');
	} else {
		passwordInput.type = 'password';
		eyeIcon.classList.replace('fa-eye-slash', 'fa-eye');
	}
}
