style:
:root {
	--primary: #6366f1;
	--background: linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%);
	--card-bg: rgba(255, 255, 255, 0.9);
	--text: #1e293b;
	--shadow: rgba(0, 0, 0, 0.1);
}

[data-theme="dark"] {
	--primary: #818cf8;
	--background: linear-gradient(135deg, #0f172a 0%, #1e293b 100%);
	--card-bg: rgba(30, 41, 59, 0.9);
	--text: #f8fafc;
	--shadow: rgba(0, 0, 0, 0.3);
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", sans-serif;
	transition: all 0.4s ease;
}

body {
	min-height: 100vh;
	display: flex;
	align-items: center;
	justify-content: center;
	background: var(--background);
	color: var(--text);
}

.card-container {
	position: relative;
	width: 400px;
	max-width: 90%;
	height: 500px;
	perspective: 1000px;
}

.auth-card {
	position: absolute;
	width: 100%;
	height: 100%;
	transform-style: preserve-3d;
	transition: transform 0.8s;
}

.auth-card.flipped {
	transform: rotateY(180deg);
}

.card-face {
	position: absolute;
	width: 100%;
	height: 100%;
	backface-visibility: hidden;
	background: var(--card-bg);
	border-radius: 1.5rem;
	padding: 2rem;
	box-shadow: 0 10px 30px var(--shadow);
	backdrop-filter: blur(10px);
}

.card-back {
	transform: rotateY(180deg);
}

.form-header {
	text-align: center;
	margin-bottom: 2rem;
}

.form-header h1 {
	font-size: 1.8rem;
	margin-bottom: 0.5rem;
}

.form-group {
	margin-bottom: 1.5rem;
	position: relative;
}

.form-group input {
	width: 100%;
	padding: 1rem;
	border: 2px solid transparent;
	border-radius: 0.8rem;
	background: rgba(255, 255, 255, 0.1);
	color: var(--text);
	padding-left: 3rem;
}

.form-group input:focus {
	outline: none;
	border-color: var(--primary);
	background: rgba(255, 255, 255, 0.2);
}

.input-icon {
	position: absolute;
	left: 1rem;
	top: 50%;
	transform: translateY(-50%);
	color: var(--text);
}

.submit-btn {
	width: 100%;
	padding: 1rem;
	background: var(--primary);
	color: white;
	border: none;
	border-radius: 0.8rem;
	font-weight: 600;
	cursor: pointer;
	transition: transform 0.2s;
}

.submit-btn:hover {
	transform: translateY(-2px);
}

.form-footer {
	text-align: center;
	margin-top: 1.5rem;
}

.form-footer a {
	color: var(--primary);
	text-decoration: none;
	cursor: pointer;
}

.theme-toggle {
	position: fixed;
	top: 20px;
	right: 20px;
	background: var(--primary);
	color: white;
	border: none;
	width: 40px;
	height: 40px;
	border-radius: 50%;
	cursor: pointer;
	display: grid;
	place-items: center;
}

@media (max-width: 480px) {
	.card-container {
		height: auto;
		perspective: none;
	}

	.auth-card {
		position: relative;
		height: auto;
	}

	.card-face {
		position: relative;
		backface-visibility: visible;
		transform: none !important;
		margin-bottom: 1rem;
	}
}

script:
// Theme Toggle
const themeToggle = document.getElementById("themeToggle");
const body = document.body;

const savedTheme = localStorage.getItem("theme") || "light";
body.setAttribute("data-theme", savedTheme);
themeToggle.innerHTML =
	savedTheme === "dark" ? '<i class="fas fa-sun"></i>' : '<i class="fas fa-moon"></i>';

themeToggle.addEventListener("click", () => {
	const currentTheme = body.getAttribute("data-theme");
	const newTheme = currentTheme === "light" ? "dark" : "light";
	body.setAttribute("data-theme", newTheme);
	localStorage.setItem("theme", newTheme);
	themeToggle.innerHTML =
		newTheme === "dark" ? '<i class="fas fa-sun"></i>' : '<i class="fas fa-moon"></i>';
});

// Card Flip Logic
const authCard = document.getElementById("authCard");
const flipToSignup = document.getElementById("flipToSignup");
const flipToLogin = document.getElementById("flipToLogin");

flipToSignup.addEventListener("click", (e) => {
	e.preventDefault();
	authCard.classList.add("flipped");
});

flipToLogin.addEventListener("click", (e) => {
	e.preventDefault();
	authCard.classList.remove("flipped");
});
