style:
:root {
	--primary-light: #f0f0f0;
	--secondary-light: #ffffff;
	--text-light: #333333;
	--accent-light: #2196f3;

	--primary-dark: #121212;
	--secondary-dark: #1e1e1e;
	--text-dark: #ffffff;
	--accent-dark: #bb86fc;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	transition: all 0.3s ease;
}

body {
	font-family: "Segoe UI", sans-serif;
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	background-color: var(--primary-light);
	color: var(--text-light);
}

body[data-theme="dark"] {
	background-color: var(--primary-dark);
	color: var(--text-dark);
}

.theme-toggle {
	position: fixed;
	top: 20px;
	right: 20px;
	background: none;
	border: none;
	cursor: pointer;
	font-size: 1.5rem;
	color: inherit;
}

.login-container {
	background: var(--secondary-light);
	padding: 2rem;
	border-radius: 1rem;
	box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
	width: 400px;
	position: relative;
}

body[data-theme="dark"] .login-container {
	background: var(--secondary-dark);
	box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
}

.login-header {
	text-align: center;
	margin-bottom: 2rem;
}

.login-header h1 {
	font-size: 1.8rem;
	margin-bottom: 0.5rem;
}

.input-group {
	margin-bottom: 1.5rem;
	position: relative;
}

.input-group input {
	width: 100%;
	padding: 1rem 1rem 1rem 3rem;
	border: 2px solid transparent;
	border-radius: 0.5rem;
	background-color: var(--primary-light);
	color: inherit;
	font-size: 1rem;
}

body[data-theme="dark"] .input-group input {
	background-color: var(--primary-dark);
}

.input-group input:focus {
	outline: none;
	border-color: var(--accent-light);
	background-color: var(--secondary-light);
}

body[data-theme="dark"] .input-group input:focus {
	border-color: var(--accent-dark);
	background-color: var(--secondary-dark);
}

.input-group label {
	position: absolute;
	left: 3rem;
	top: 50%;
	transform: translateY(-50%);
	pointer-events: none;
	color: #666;
	transition: all 0.3s ease;
}

.input-group input:focus ~ label,
.input-group input:not(:placeholder-shown) ~ label {
	top: -10px;
	left: 0.5rem;
	font-size: 0.8rem;
	color: var(--accent-light);
	background: var(--secondary-light);
	padding: 0 0.3rem;
}

.input-group i {
	position: absolute;
	left: 1rem;
	top: 50%;
	transform: translateY(-50%);
}

body[data-theme="dark"] .input-group input:focus ~ label,
body[data-theme="dark"] .input-group input:not(:placeholder-shown) ~ label {
	color: var(--accent-dark);
	background: var(--secondary-dark);
}

#password-input-group i:last-child {
	left: auto;
	right: 1rem;
}

.password-toggle {
	position: absolute;
	right: 1rem;
	top: 50%;
	transform: translateY(-50%);
	cursor: pointer;
	color: #666;
}

.password-toggle:hover {
	color: var(--accent-light);
}

body[data-theme="dark"] .password-toggle:hover {
	color: var(--accent-dark);
}

button[type="submit"] {
	width: 100%;
	padding: 1rem;
	background-color: var(--accent-light);
	color: white;
	border: none;
	border-radius: 0.5rem;
	font-size: 1rem;
	cursor: pointer;
}

body[data-theme="dark"] button[type="submit"] {
	background-color: var(--accent-dark);
}

button[type="submit"]:hover {
	opacity: 0.9;
}

@media (max-width: 480px) {
	.login-container {
		width: 90%;
		padding: 1.5rem;
	}
}

script:
// Theme toggle functionality
const themeToggle = document.getElementById("themeToggle");
const body = document.body;

// Check for saved theme
const savedTheme = localStorage.getItem("theme");
if (savedTheme) {
	body.setAttribute("data-theme", savedTheme);
	updateToggleIcon(savedTheme);
}

themeToggle.addEventListener("click", () => {
	const currentTheme = body.getAttribute("data-theme");
	const newTheme = currentTheme === "light" ? "dark" : "light";

	body.setAttribute("data-theme", newTheme);
	localStorage.setItem("theme", newTheme);
	updateToggleIcon(newTheme);
});

function updateToggleIcon(theme) {
	themeToggle.innerHTML =
		theme === "light" ? '<i class="fas fa-moon"></i>' : '<i class="fas fa-sun"></i>';
}

function togglePassword() {
	const password = document.getElementById("password");
	const eyeIcon = document.querySelector(".password-toggle");

	if (password.type === "password") {
		password.type = "text";
		eyeIcon.classList.replace("fa-eye", "fa-eye-slash");
	} else {
		password.type = "password";
		eyeIcon.classList.replace("fa-eye-slash", "fa-eye");
	}
}
