style:
:root {
	--primary-color: #2563eb;
	--background-color: #f8fafc;
	--text-color: #1e293b;
	--input-bg: #ffffff;
	--border-color: #e2e8f0;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Segoe UI", system-ui, sans-serif;
}

body {
	background-color: var(--background-color);
	color: var(--text-color);
	min-height: 100vh;
	display: grid;
	place-items: center;
}

.login-container {
	background: white;
	padding: 2.5rem;
	border-radius: 1rem;
	box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
	width: 100%;
	max-width: 400px;
	margin: 1rem;
}

.login-header {
	text-align: center;
	margin-bottom: 2rem;
}

.login-header h1 {
	font-size: 1.5rem;
	margin-bottom: 0.5rem;
}

.form-group {
	margin-bottom: 1.5rem;
	position: relative;
}

.form-input {
	width: 100%;
	padding: 1rem;
	border: 1px solid var(--border-color);
	border-radius: 0.5rem;
	background: var(--input-bg);
	padding-left: 2.5rem;
}

.form-input:focus {
	outline: none;
	border-color: var(--primary-color);
	box-shadow: 0 0 0 3px rgba(37, 99, 235, 0.1);
}

.input-icon {
	position: absolute;
	left: 1rem;
	top: 50%;
	transform: translateY(-50%);
	color: var(--text-color);
}

.submit-btn {
	width: 100%;
	padding: 1rem;
	background: var(--primary-color);
	color: white;
	border: none;
	border-radius: 0.5rem;
	font-weight: 600;
	cursor: pointer;
	transition: opacity 0.2s;
}

.submit-btn:hover {
	opacity: 0.9;
}

.additional-links {
	margin-top: 1.5rem;
	text-align: center;
}

.additional-links a {
	color: var(--primary-color);
	text-decoration: none;
}

.additional-links a:hover {
	text-decoration: underline;
}

.icons {
	display: none;
}
