style:
/* Global Reset */
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

:root {
	--bg-color: #1b1b1b;
	--clip-size: 0.8rem;
}

body {
	display: flex;
	align-items: center;
	justify-content: center;
	min-height: 100dvh;
	font-family: Arial, Helvetica, sans-serif;
	background-color: var(--bg-color);
}

nav {
	background-color: aliceblue;
	width: 578px;
	height: 4.8rem;
	display: flex;
	align-items: center;
	justify-content: center;
	border-radius: 0.8rem 0.8rem 0 0;
}

@media screen and (max-width: 768px) {
	nav {
		max-width: 578px;
		width: 100%;
	}
}

ul {
	width: 100%;
	max-width: 578px;
	list-style: none;
	display: flex;
	justify-content: space-between;
	padding: 20px;
	position: relative;
}

ul li {
	position: relative;
	width: 4.8rem;
	height: 4.8rem;
	z-index: 1;
}

ul li a {
	position: relative;
	display: flex;
	align-items: center;
	justify-content: center;
	flex-direction: column;
	width: 100%;
	text-decoration: none;
	color: var(--bg-color);
	cursor: pointer;
}

.icon {
	position: relative;
	display: block;
	line-height: 4.8rem;
	font-size: 1.6rem;
	text-align: center;
	transition: 0.5s ease;
	color: var(--bg-color);
}

li.active .icon {
	transform: translateY(-2.4rem);
	color: aliceblue;
}

span.text {
	position: absolute;
	transition: 0.5s ease;
	opacity: 0;
	transform: translateY(1.2rem);
}

li.active span.text {
	opacity: 1;
}

.indicator {
	position: absolute;
	width: 4.8rem;
	height: 4.8rem;
	background-color: #7c3aed;
	border-radius: 50%;
	border: 0.4rem solid aliceblue;
	top: 50%;
	transform: translate(-50%, -100%);
	transition: left 0.5s ease;
}

.indicator::before {
	content: "";
	position: absolute;
	width: var(--clip-size);
	height: var(--clip-size);
	top: 35%;
	left: -1rem;
	background-color: aliceblue;
	clip-path: url(#smoothClipBefore);
	transition: clip-path 0.5s ease;
}

.indicator::after {
	content: "";
	position: absolute;
	width: var(--clip-size);
	height: var(--clip-size);
	top: 35%;
	right: calc(-25%);
	background-color: aliceblue;
	clip-path: url(#smoothClipAfter);
	transition: clip-path 0.5s ease;
}

script:
const tabLinks = document.querySelectorAll(".tab-link");
const indicator = document.querySelector(".indicator");

tabLinks.forEach((link, index) => {
	link.addEventListener("click", () => {
		tabLinks.forEach((e) => e.classList.remove("active"));
		link.classList.add("active");
		slideIndicator();
	});
});

function slideIndicator() {
	// Find the active tab
	const activeTab = document.querySelector(".tab-link.active");
	if (!activeTab) return;

	// Calculate the center of the active tab relative to its parent (ul)
	const parentRect = activeTab.parentElement.getBoundingClientRect();
	const tabRect = activeTab.getBoundingClientRect();
	const tabCenter = tabRect.left - parentRect.left + tabRect.width / 2;

	// Update the indicator's left position. The CSS transform will ensure it remains centered.
	indicator.style.left = `${tabCenter}px`;
}

// Recalculate position on window resize
window.addEventListener("resize", slideIndicator);

// Initialize on page load
window.addEventListener("DOMContentLoaded", slideIndicator);
