style:
/* Global Reset */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

:root {
  --bg-color: rgb(27, 27, 27);
  --clip-size: 1.2rem;
}

body {
  display: flex;
  align-items: center;
  justify-content: center;
  min-height: 100dvh;
  font-family: Arial, Helvetica, sans-serif;
  background-color: var(--bg-color);
}

nav {
  background-color: aliceblue;
  width: 578px;
  height: 4.8rem;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 0.8rem 0.8rem 0 0;
}

@media screen and (max-width: 768px) {
  nav {
    max-width: 100%;
    width: 100%;
  }
}

ul {
  width: 100%;
  list-style: none;
  display: flex;
  justify-content: space-between;
  padding: 20px;
  position: relative;
}

ul li {
  position: relative;
  width: 4.8rem;
  height: 4.8rem;
  z-index: 1;
}

ul li a {
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  width: 100%;
  height: 100%;
  text-decoration: none;
  color: var(--bg-color);
  cursor: pointer;
}

.icon {
  display: block;
  font-size: 1.6rem;
  transition: 0.5s ease;
  color: var(--bg-color);
}

li.active .icon {
  color: blue;
}

script:
const tabLinks = document.querySelectorAll(".tab-link");

tabLinks.forEach((link, index) => {
  link.addEventListener("click", () => {
    tabLinks.forEach((e) => {
      e.classList.remove("active");
    });
    link.classList.add("active");
  });
});
