style:
/* Reset */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

:root {
  --sidebar-width: 280px;
  --sidebar-bg: linear-gradient(160deg, #2b2d42 0%, #1a1b2f 100%);
  --text-color: #edf2f4;
  --accent-color: #ef233c;
  --hover-bg: rgba(255, 255, 255, 0.05);
  --transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
}

body {
  font-family: "Segoe UI", system-ui, sans-serif;
  background: #f8f9fa;
  min-height: 100vh;
  margin-left: 80px;
  transition: var(--transition);
}

/* ---------- Sidebar Styles ---------- */
.sidebar {
  width: var(--sidebar-width);
  height: 100vh;
  background: var(--sidebar-bg);
  position: fixed;
  top: 0;
  left: -280px;
  padding: 1.5rem;
  box-shadow: 4px 0 15px rgba(0, 0, 0, 0.1);
  transition: var(--transition);
  z-index: 1000;
}

.sidebar.active {
  left: 0;
}

.logo {
  display: flex;
  align-items: center;
  gap: 1rem;
  padding: 0 1rem 2rem;
  margin-bottom: 1rem;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.logo-icon {
  width: 36px;
  height: 36px;
  fill: var(--accent-color);
}

.logo-text {
  color: var(--text-color);
  font-size: 1.5rem;
  font-weight: 600;
  opacity: 0;
  transition: opacity 0.3s ease;
}

.nav-list {
  list-style: none;
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}

.nav-item {
  position: relative;
}

.nav-link {
  display: flex;

  cursor: pointer;
 align-items: center;
  gap: 1.5rem;
  padding: 1rem;
  border-radius: 8px;
  color: var(--text-color);
  text-decoration: none;
  transition: var(--transition);
}

.nav-link:hover {
  background: var(--hover-bg);
  transform: translateX(8px);
}

/* Enhanced Active State */
.nav-link.active {
  background: var(--accent-color);
  border-left: 4px solid var(--text-color);
  padding-left: calc(1rem - 4px);
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transform: translateX(8px) scale(1.03);
}

.icon {
  width: 24px;
  height: 24px;
  fill: currentColor;
  transition: transform 0.3s ease;
}

.link-text {
  font-size: 1rem;
  font-weight: 500;
  opacity: 0;
  transition: opacity 0.3s ease;
}

/* ---------- Toggle Button ---------- */
.toggle-btn {
  position: fixed;
  left: 1.5rem;
  top: 1.5rem;
  background: var(--sidebar-bg);
  border: none;
  border-radius: 8px;
  padding: 0.75rem;
  cursor: pointer;
  z-index: 1100;
  transition: var(--transition);
}

.hamburger {
  cursor: pointer;
  z-index: 1100;
}

.hamburger .bar {
  width: 25px;
  height: 3px;
  background: #fff;
  margin: 5px 0;
  transition: 0.3s;
}

/* ---------- Active States ---------- */
.sidebar.active {
  left: 0;
}

.sidebar.active .logo-text,
.sidebar.active .link-text {
  opacity: 1;
}

.sidebar.active + body {
  margin-left: var(--sidebar-width);
}

.toggle-btn.active .bar:nth-child(1) {
  transform: translateY(8px) rotate(45deg);
}

.toggle-btn.active .bar:nth-child(2) {
  opacity: 0;
}

.toggle-btn.active .bar:nth-child(3) {
  transform: translateY(-8px) rotate(-45deg);
}

script:
const sidebar = document.getElementById("sidebar");
const toggleBtn = document.getElementById("toggleBtn");
const links = sidebar.querySelectorAll(".nav-link");

links.forEach((link) => {
  link.addEventListener("click", () => {
    links.forEach((item) => item.classList.remove("active"));
    link.classList.add("active");
  });
});

toggleBtn.addEventListener("click", () => {
  sidebar.classList.toggle("active");
  toggleBtn.classList.toggle("active");
  toggleBtn.style.transform = sidebar.classList.contains("active")
    ? "translateX(240px) rotate(90deg)"
    : "none";
});
