style:
/* Reset */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

:root {
  --sidebar-bg: #2c3e50;
  --text-color: #ecf0f1;
  --hover-bg: #34495e;
  --icon-size: 1.8rem;
  --transition: all 0.3s ease;
}

body {
  font-family: "Arial", sans-serif;
  margin-left: 80px;
  transition: var(--transition);
}

.sidebar {
  width: 80px;
  height: 100vh;
  background: var(--sidebar-bg);
  position: fixed;
  top: 0;
  left: 0;
  padding: 20px 0;
  overflow-x: hidden;
  transition: var(--transition);
  z-index: 1000;
}

.sidebar.active {
  width: 250px;
}

.toggle-btn {
  background: none;
  border: none;
  color: var(--text-color);
  font-size: 1.5rem;
  cursor: pointer;
  padding: 15px;
  width: 100%;
  text-align: left;
}

.nav-list {
  list-style: none;
  padding: 20px 0;
}

.nav-item {
  position: relative;
}

.nav-link {
  display: flex;
  align-items: center;
  padding: 15px 20px;
  color: var(--text-color);
  text-decoration: none;
  white-space: nowrap;
  transition: background 0.3s ease;
}

.nav-link:hover {
  background: var(--hover-bg);
}

.nav-icon {
  font-size: var(--icon-size);
  min-width: 40px;
  display: flex;
  justify-content: center;
}

.nav-text {
  margin-left: 20px;
  opacity: 0;
  transition: opacity 0.2s ease 0.1s;
}

.sidebar.active .nav-text {
  opacity: 1;
}

/* Toggle Icon Animation */
.toggle-icon {
  transition: var(--transition);
}

.sidebar.active .toggle-icon {
  transform: rotate(180deg);
}

@media (max-width: 768px) {
  body {
    margin-left: 0;
    padding-top: 60px;
  }

  .sidebar {
    width: 100%;
    height: 60px;
    padding: 0;
  }

  .sidebar.active {
    height: 100vh;
    width: 250px;
  }

  .nav-list {
    display: none;
  }

  .sidebar.active .nav-list {
    display: block;
  }

  .toggle-btn {
    text-align: center;
    padding: 15px 0;
  }
}

script:
const sidebar = document.getElementById("sidebar");
const toggleBtn = document.getElementById("toggleBtn");

toggleBtn.addEventListener("click", () => {
  sidebar.classList.toggle("active");
});
