style:
:root {
	--bg-primary: #ffffff;
	--bg-secondary: #f8f9fa;
	--text-primary: #1a1a1a;
	--text-secondary: #6c757d;
	--border-color: #dee2e6;
	--hover-bg: #e9ecef;
	--active-bg: #dfe3e6;
	--tooltip-bg: #2d2d2d;
	--tooltip-text: #ffffff;
	--transition-speed: 0.3s;
}

.dark-theme {
	--bg-primary: #1a1d28;
	--bg-secondary: #252936;
	--text-primary: #e4e6eb;
	--text-secondary: #8d8f93;
	--border-color: #3a3d45;
	--hover-bg: #2d3038;
	--active-bg: #373a44;
	--tooltip-bg: #e4e6eb;
	--tooltip-text: #1a1d28;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

body {
	font-family: "Segoe UI", system-ui, sans-serif;
	background: var(--bg-secondary);
	min-height: 100vh;
	transition: background-color var(--transition-speed);
}

.sidebar {
	width: 260px;
	height: 100vh;
	background: var(--bg-primary);
	position: fixed;
	top: 0;
	left: 0;
	display: flex;
	flex-direction: column;
	transition: width var(--transition-speed);
	box-shadow: 2px 0 12px rgba(0, 0, 0, 0.05);
}

.sidebar-header {
	padding: 1.5rem;
	display: flex;
	align-items: center;
	justify-content: space-between;
	border-bottom: 1px solid var(--border-color);
}

.logo {
	font-size: 1.25rem;
	font-weight: 600;
	color: var(--text-primary);
	white-space: nowrap;
	overflow: hidden;
	transition: opacity var(--transition-speed);
}

.collapse-btn {
	background: none;
	border: none;
	color: var(--text-primary);
	cursor: pointer;
	padding: 0.5rem;
	border-radius: 6px;
	transition: background-color var(--transition-speed);
}

.collapse-btn:hover {
	background: var(--hover-bg);
}

.sidebar-nav {
	flex-grow: 1;
	padding: 1rem;
}

.nav-item {
	list-style: none;
	margin-bottom: 0.5rem;
}

.nav-link {
	display: flex;
	align-items: center;
	gap: 1rem;
	padding: 0.8rem;
	color: var(--text-primary);
	text-decoration: none;
	border-radius: 8px;
	transition: background-color var(--transition-speed), transform var(--transition-speed);
	position: relative;
	cursor: pointer;
}

.nav-link:hover {
	background: var(--hover-bg);
	transform: translateX(5px);
}

.nav-link.active {
	background: var(--active-bg);
	font-weight: 500;
}

.nav-icon {
	font-size: 1.1rem;
	width: 24px;
	display: flex;
	justify-content: center;
}

.nav-text {
	white-space: nowrap;
	overflow: hidden;
	transition: opacity var(--transition-speed);
}

.tooltip {
	position: absolute;
	left: calc(100% + 15px);
	background: var(--tooltip-bg);
	color: var(--tooltip-text);
	padding: 6px 12px;
	border-radius: 6px;
	font-size: 0.875rem;
	opacity: 0;
	visibility: hidden;
	transition: opacity var(--transition-speed);
	pointer-events: none;
	white-space: nowrap;
	z-index: 999;
}

.tooltip::before {
	content: "";
	position: absolute;
	right: 100%;
	top: 50%;
	transform: translateY(-50%);
	border-width: 5px;
	border-style: solid;
	border-color: transparent var(--tooltip-bg) transparent transparent;
}

/* Collapsed state */
.sidebar.collapsed {
	width: 80px;
}

.sidebar.collapsed .logo {
	opacity: 0;
}

.sidebar.collapsed .nav-text {
	opacity: 0;
}

.sidebar.collapsed .nav-link:hover .tooltip {
	opacity: 1;
	visibility: visible;
}

.theme-switcher {
	padding: 1.5rem;
	border-top: 1px solid var(--border-color);
}

.theme-toggle {
	display: flex;
	align-items: center;
	gap: 0.75rem;
	padding: 0.75rem;
	border-radius: 8px;
	background: var(--bg-secondary);
	border: none;
	cursor: pointer;
	color: var(--text-primary);
	transition: background-color var(--transition-speed);
}

.theme-toggle:hover {
	background: var(--hover-bg);
}

.toggle-icon {
	font-size: 1.1rem;
}

script:
const navLinks = document.querySelectorAll(".nav-link");

function toggleSidebar() {
	const sidebar = document.querySelector(".sidebar");
	const collapseBtn = document.querySelector(".collapse-btn i");

	sidebar.classList.toggle("collapsed");
	collapseBtn.classList.toggle("fa-angle-left");
	collapseBtn.classList.toggle("fa-angle-right");
}

function toggleNavActive(element) {
	navLinks.forEach((item) => item.classList.remove("active"));
	element.classList.add("active");
}

function toggleTheme() {
	document.body.classList.toggle("dark-theme");
	const themeToggle = document.querySelector(".theme-toggle i");
	themeToggle.classList.toggle("fa-moon");
	themeToggle.classList.toggle("fa-sun");
}
