style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

body {
	font-family: "Inter", system-ui, -apple-system, sans-serif;
	background: #0f0f0f;
	min-height: 100vh;
	display: flex;
	align-items: center;
}

.slider-container {
	position: relative;
	max-width: 1400px;
	margin: 0 auto;
	padding: 40px 0;
	overflow: hidden;
}

.slider-track {
	display: flex;
	align-items: center;
	justify-content: center;
	transition: transform 0.6s cubic-bezier(0.16, 1, 0.3, 1);
	padding: 20px 0;
}

.slide-card {
	flex: 0 0 340px;
	margin: 0 15px;
	background: #1a1a1a;
	border-radius: 20px;
	overflow: hidden;
	transform-style: preserve-3d;
	transition: transform 0.6s, opacity 0.3s;
	position: relative;
	cursor: pointer;
	opacity: 0.6;
}

.slide-card.active {
	transform: scale(1.05);
	opacity: 1;
	z-index: 2;
}

.card-image {
	height: 240px;
	position: relative;
	overflow: hidden;
}

.card-image img {
	width: 100%;
	height: 100%;
	object-fit: cover;
	transition: transform 0.6s;
}

.card-content {
	padding: 25px;
	color: white;
}

.card-title {
	font-size: 1.4rem;
	margin-bottom: 15px;
}

.card-description {
	font-size: 0.95rem;
	opacity: 0.8;
	line-height: 1.6;
}

.nav-button {
	position: absolute;
	top: 50%;
	transform: translateY(-50%);
	width: 50px;
	height: 50px;
	background: rgba(255, 255, 255, 0.1);
	border: none;
	border-radius: 50%;
	cursor: pointer;
	transition: all 0.3s ease;
	display: flex;
	align-items: center;
	justify-content: center;
}

.nav-button:hover {
	background: rgba(255, 255, 255, 0.2);
	transform: translateY(-50%) scale(1.1);
}

.prev-button {
	left: 30px;
}

.next-button {
	right: 30px;
}

.dots-container {
	position: absolute;
	bottom: 20px;
	left: 50%;
	transform: translateX(-50%);
	display: flex;
	gap: 10px;
}

.dot {
	width: 10px;
	height: 10px;
	border-radius: 50%;
	background: rgba(255, 255, 255, 0.3);
	cursor: pointer;
	transition: all 0.3s ease;
}

.dot.active {
	background: white;
	transform: scale(1.3);
}

@media (max-width: 768px) {
	.slide-card {
		flex: 0 0 280px;
		margin: 0 10px;
	}

	.card-image {
		height: 200px;
	}

	.nav-button {
		width: 40px;
		height: 40px;
	}
}

@media (max-width: 480px) {
	.slider-container {
		padding: 20px 0;
	}

	.slide-card {
		flex: 0 0 220px;
		margin: 0 8px;
	}

	.card-content {
		padding: 20px;
	}

	.card-title {
		font-size: 1.2rem;
	}

	.nav-button {
		width: 35px;
		height: 35px;
	}
}

script:
const track = document.querySelector(".slider-track");
const cards = document.querySelectorAll(".slide-card");
const prevBtn = document.querySelector(".prev-button");
const nextBtn = document.querySelector(".next-button");
const dotsContainer = document.querySelector(".dots-container");
let cardWidth = cards[0].offsetWidth + 30;
let isAnimating = false;

let activeCard = Math.round((cards.length - 1) / 2);

const dots = document.querySelectorAll(".dot");

dots.forEach((dot, index) => {
	dot.addEventListener("click", (e) => {
		activeCard = index;
	});
});

function updateElements() {
	cards.forEach((card, index) => {
		card.classList.toggle("active", index === activeCard);
	});

	dots.forEach((dot, index) => {
		dot.classList.toggle("active", index === activeCard);
	});
}

let offset = 0;
function goToSlide(index, direction) {
	if (isAnimating) return;
	isAnimating = true;

	if (direction === "next") {
		offset -= cardWidth;
	} else if (direction === "prev") {
		offset += cardWidth;
	}
	track.style.transform = `translateX(${offset}px)`;

	activeCard = (index + cards.length) % cards.length;

	updateElements();
	updateSlideBtn(index);

	setTimeout(() => {
		isAnimating = false;
	}, 600);
}

function updateSlideBtn(index) {
	if (index === 0) {
		prevBtn.style.display = "none";
	} else if (index === cards.length - 1) {
		nextBtn.style.display = "none";
	} else {
		prevBtn.style.display = "flex";
		nextBtn.style.display = "flex";
	}
}

prevBtn.addEventListener("click", () => goToSlide(activeCard - 1, "prev"));
nextBtn.addEventListener("click", () => goToSlide(activeCard + 1, "next"));
window.addEventListener("resize", handleResize);

function handleResize() {
	cardWidth = cards[0].offsetWidth + 30;
	goToSlide(activeCard);
}

updateElements();
