style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

body {
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	background: #1a1a1a;
	font-family: Arial, sans-serif;
	overflow: hidden;
}

.carousel-container {
	position: relative;
	width: 100%;
	max-width: 1200px;
	height: 70vh;
}

.carousel {
	position: absolute;
	width: 100%;
	height: 100%;
	transform-style: preserve-3d;
	transition: transform 1s cubic-bezier(0.23, 1, 0.32, 1);
	display: flex;
	align-items: center;
	justify-content: center;
}

.slide {
	position: absolute;
	width: 400px;
	height: 500px;
	background: #fff;
	border-radius: 20px;
	overflow: hidden;
	box-shadow: 0 15px 30px rgba(0, 0, 0, 0.3);
	transition: all 0.5s ease;
	cursor: pointer;
}

.slide img {
	width: 100%;
	height: 70%;
	object-fit: cover;
}

.slide-content {
	padding: 1.5rem;
	background: white;
}

.nav-btn {
	position: absolute;
	top: 50%;
	transform: translateY(-50%);
	width: 50px;
	height: 50px;
	background: rgba(255, 255, 255, 0.9);
	border: none;
	border-radius: 50%;
	cursor: pointer;
	box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
	transition: all 0.3s ease;
	z-index: 2;
}

.nav-btn:hover {
	transform: translateY(-50%) scale(1.1);
}

.prev {
	left: 5%;
}

.next {
	right: 5%;
}

.dots {
	position: absolute;
	bottom: 2rem;
	left: 50%;
	transform: translateX(-50%);
	display: flex;
	gap: 1rem;
}

.dot {
	width: 12px;
	height: 12px;
	border-radius: 50%;
	background: rgba(255, 255, 255, 0.4);
	border: none;
	cursor: pointer;
	transition: all 0.3s ease;
}

.dot.active {
	background: white;
	transform: scale(1.4);
}

@media (max-width: 768px) {
	.slide {
		width: 300px;
		height: 400px;
	}

	.nav-btn {
		width: 40px;
		height: 40px;
	}
}

script:
const carousel = document.querySelector(".carousel");
const slides = document.querySelectorAll(".slide");
const dotsContainer = document.querySelector(".dots");
const prevBtn = document.querySelector(".prev");
const nextBtn = document.querySelector(".next");
let currentIndex = 0;
let autoRotateInterval;

// Initialize carousel
function initializeCarousel() {
	const totalSlides = slides.length;
	const angle = 360 / totalSlides;

	slides.forEach((slide, index) => {
		const rotateY = angle * index;
		slide.style.transform = `rotateY(${rotateY}deg) translateZ(600px)`;
	});

	createDots();
	startAutoRotate();
}

// Create navigation dots
function createDots() {
	slides.forEach((_, index) => {
		const dot = document.createElement("button");
		dot.className = "dot";
		if (index === 0) dot.classList.add("active");
		dot.addEventListener("click", () => rotateTo(index));
		dotsContainer.appendChild(dot);
	});
}

// Rotate carousel
function rotateTo(index) {
	currentIndex = index;
	const angle = (360 / slides.length) * index;
	carousel.style.transform = `rotateY(-${angle}deg)`;
	updateDots();
}

// Update active dot
function updateDots() {
	document.querySelectorAll(".dot").forEach((dot, index) => {
		dot.classList.toggle("active", index === currentIndex);
	});
}

// Auto-rotation
function startAutoRotate() {
	autoRotateInterval = setInterval(() => {
		currentIndex = (currentIndex + 1) % slides.length;
		rotateTo(currentIndex);
	}, 5000);
}

// Event listeners
prevBtn.addEventListener("click", () => {
	currentIndex = (currentIndex - 1 + slides.length) % slides.length;
	rotateTo(currentIndex);
	resetAutoRotate();
});

nextBtn.addEventListener("click", () => {
	currentIndex = (currentIndex + 1) % slides.length;
	rotateTo(currentIndex);
	resetAutoRotate();
});

function resetAutoRotate() {
	clearInterval(autoRotateInterval);
	startAutoRotate();
}

// Touch events
let touchStartX = 0;
carousel.addEventListener("touchstart", (e) => {
	touchStartX = e.touches[0].clientX;
});

carousel.addEventListener("touchend", (e) => {
	const touchEndX = e.changedTouches[0].clientX;
	if (touchStartX - touchEndX > 50) {
		currentIndex = (currentIndex + 1) % slides.length;
		rotateTo(currentIndex);
	} else if (touchEndX - touchStartX > 50) {
		currentIndex = (currentIndex - 1 + slides.length) % slides.length;
		rotateTo(currentIndex);
	}
});

// Keyboard navigation
document.addEventListener("keydown", (e) => {
	if (e.key === "ArrowLeft") prevBtn.click();
	if (e.key === "ArrowRight") nextBtn.click();
});

// Initialize
initializeCarousel();
