style:
:root {
	--white-100: hsl(206, 5%, 100%);
	--white-200: hsl(206, 5%, 90%);
	--black-300: hsl(210, 20%, 6%);
	--black-500: hsl(210, 20%, 1%);
}

*,
*::before,
*::after {
	padding: 0;
	margin: 0;
	box-sizing: border-box;
}

body {
	font-family: sans-serif;
	background-color: var(--white-100);
}

.slider-section {
	padding: 5rem 1.25rem;
	max-width: 75rem;
	margin: 0 auto;
}

.slider-container {
	position: relative;
	overflow: hidden;
}

.slider {
	display: flex;
	align-items: center;
	justify-content: flex-start;
	-ms-overflow-style: none;
	scrollbar-width: none;
	overflow-x: auto;
	overflow-y: hidden;
	overscroll-behavior-inline: contain;
	padding: 10px;
	scroll-padding-left: 10px;
	gap: 1%;
}

.snaps-inline {
	scroll-snap-type: inline mandatory;
}

.snaps-inline > * {
	scroll-snap-align: start;
}

.slider-img {
	flex: 1 0 48%;
	max-height: 600px;
}

.slider-img img {
	width: 100%;
	height: 100%;
	object-fit: cover;
	border-radius: 8px;
}

.slider-nav-prev,
.slider-nav-next {
	position: absolute;
	top: 50%;
	transform: translateY(-50%);
	width: 40px;
	height: 40px;
	background: var(--black-300);
	border-radius: 50%;
	cursor: pointer;
	z-index: 10;
	display: flex;
	align-items: center;
	justify-content: center;
	color: var(--white-100);
	font-weight: bold;
	opacity: 0.75;
	transition: opacity 0.3s ease;
}

.slider-nav-prev:hover,
.slider-nav-next:hover {
	opacity: 1;
}

.slider-nav-prev {
	left: 20px;
}

.slider-nav-next {
	right: 20px;
}

script:
document.addEventListener("DOMContentLoaded", () => {
	const slider = document.querySelector(".slider");
	const slides = document.querySelectorAll(".slide-img");
	const prevBtn = document.querySelector(".slider-nav-prev");
	const nextBtn = document.querySelector(".slider-nav-next");

	let currentIndex = 0;

	function updateSliderPosition(direction) {
		const slideWidth = slides[0].clientWidth;
		const maxScrollLeft = slider.scrollWidth - slider.clientWidth;

		if (direction === "prev") {
			if (slider.scrollLeft === 0) return;
			slider.scrollBy({
				left: -slideWidth,
				behavior: "smooth",
			});
		} else if (direction === "next") {
			if (slider.scrollLeft === maxScrollLeft) return;
			slider.scrollBy({
				left: slideWidth,
				behavior: "smooth",
			});
		}
	}

	prevBtn.addEventListener("click", () => {
		currentIndex = currentIndex === 0 ? slides.length - 1 : currentIndex - 1;
		updateSliderPosition("prev");
	});

	nextBtn.addEventListener("click", () => {
		currentIndex = currentIndex === slides.length - 1 ? 0 : currentIndex + 1;
		updateSliderPosition("next");
	});

	window.addEventListener("resize", updateSliderPosition);
});
