style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

body {
	display: flex;
	align-items: center;
	justify-content: center;
	width: 100dvw;
}

.slider-container {
	display: flex;
	align-items: center;
	justify-content: center;
	width: 100%;
	height: 80dvh;
	padding-bottom: 48px;
}

.slider-input {
	display: none;
	opacity: 0;
	visibility: hidden;
}

.slides {
	position: relative;
	display: flex;
	align-items: flex-end;
	justify-content: center;
	height: 100%;
	width: 90%;
	max-width: 90%;
}

.slider-navigations {
	display: flex;
	width: 100%;
	gap: 20px;
	align-items: flex-end;
	justify-content: center;
}

.slider-navigation {
	display: flex;
	max-width: 200px;
	max-height: 140px;
	cursor: pointer;
	border: 2px solid wheat;
	border-radius: 12px;
	overflow: hidden;
	opacity: 0.8;
	transition: opacity 0.4s ease;
	transform: all 0.2s ease;
}

.slider-navigation:hover {
	opacity: 1;
	transform: scale(1.1);
}

.slider-navigation img {
	width: 100%;
	height: 100%;
	object-fit: cover;
}

#slider-input-1:checked ~ .slides .slider-navigations label:nth-child(1),
#slider-input-2:checked ~ .slides .slider-navigations label:nth-child(2),
#slider-input-3:checked ~ .slides .slider-navigations label:nth-child(3),
#slider-input-4:checked ~ .slides .slider-navigations label:nth-child(4) {
	opacity: 1;
	transform: scale(1.1);
	border-color: gold;
}

#slider-input-1:checked ~ .slides { background: url("https://picsum.photos/id/230/1200/1200") no-repeat center; background-size: cover; }
#slider-input-2:checked ~ .slides { background: url("https://picsum.photos/id/231/1200/1200") no-repeat center; background-size: cover; }
#slider-input-3:checked ~ .slides { background: url("https://picsum.photos/id/232/1200/1200") no-repeat center; background-size: cover; }
#slider-input-4:checked ~ .slides { background: url("https://picsum.photos/id/233/1200/1200") no-repeat center; background-size: cover; }
