style:
@import url("https://fonts.googleapis.com/css2?family=Rufina:wght@400;700&family=Sansita+Swashed:wght@300..900&display=swap");
@import url("https://fonts.googleapis.com/css2?family=Lobster+Two:ital,wght@0,400;0,700;1,400;1,700&display=swap");
@import url("https://fonts.googleapis.com/css2?family=Playfair+Display:ital,wght@0,400..900;1,400..900&display=swap");

:root {
	--item-gap: 15px;
	--text-font: "Lobster Two", serif;
	--text-font-size: 20px;
	--line-color: #7a5937;
	--paragraph-font: "Playfair Display";
	--card-background-color: wheat;
	--text-light: #5b4636;
	--primary-dark: #8d6e63;
	--secondary-dark: #4e342e;
	--text-dark: #f8e5d0;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

.snaps-inline {
	scroll-snap-type: inline mandatory;
}

.snaps-inline > * {
	scroll-snap-align: start;
}

.slider-container {
	display: flex;
	justify-content: space-between;
	align-items: center;
	max-width: 100%;
	width: 100%;
}

.next-btn,
.prev-btn {
	display: flex;
	justify-content: center;
	cursor: pointer;
	width: 5%;
	transition: opacity 0.3s ease-in-out;
}

.next-btn svg,
.prev-btn svg {
	width: 50px;
	height: 50px;
}

.next-btn svg path,
.prev-btn svg path {
	transition: stroke 0.3s ease-in;
	stroke: var(--line-color);
	stroke-width: 2px;
}

.next-btn:hover svg path,
.prev-btn:hover svg path {
	stroke: var(--secondary-dark);
}

.slides-area {
	width: 90%;
	display: flex;
	align-items: center;
	overflow-x: auto;
	overflow-y: hidden;
	scrollbar-width: none;
	-ms-overflow-style: none;
	overscroll-behavior-inline: contain;
	padding: 10px;
	gap: 1%;
	scroll-padding-left: 10px;
}

.slides-area > article {
	scroll-snap-align: start;
}

.slide {
	flex: 1 0 32.6%;
	height: 410px;
	background-color: var(--card-background-color);
	border-radius: 10px;
	overflow: hidden;
	box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
	transition: transform 0.3s ease-in-out;
}

.slide:hover {
	transform: scale(1.02);
}

.slide-img {
	width: 100%;
	height: 220px;
}

.slide-img img {
	width: 100%;
	height: 100%;
	object-fit: cover;
}

.slide-content {
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: space-around;
	height: 170px;
	padding: 10px;
	text-align: center;
}

.slide-content h5 {
	height: 30px;
	line-height: 30px;
	font-family: var(--text-font);
	font-size: var(--text-font-size);
}

.slide-content p {
	word-break: break-word;
	text-align: start;
	height: 115px;
	overflow: hidden;
	text-overflow: ellipsis;
	display: -webkit-box;
	-webkit-line-clamp: 4;
	line-clamp: 4;
	-webkit-box-orient: vertical;
	font-family: var(--paragraph-font);
}

.slide-details {
	width: 100%;
	height: 30px;
	display: flex;
	align-items: center;
	justify-content: space-around;
}

.date-icon,
.comment-icon {
	width: 20px;
	height: 20px;
}

.date-icon svg,
.comment-icon svg {
	width: 20px;
	height: 20px;
}

.date,
.comments {
	display: flex;
	align-items: center;
	gap: 5px;
}

/* Responsive Design */
@media (max-width: 1200px) {
	.slides-area {
		width: 85%;
	}
}

@media (max-width: 768px) {
	.next-btn,
	.prev-btn {
		display: none;
	}

	.slides-area {
		width: 100%;
	}

	.slide {
		flex: 1 0 45%;
	}

	.slide-content h5 {
		height: 15px;
		line-height: 15px;
		font-size: 15px;
	}

	.slide-content p {
		font-size: 14px;
	}

	.date,
	.comments {
		font-size: 12px;
	}
}

@media (max-width: 480px) {
	.slide {
		flex: 1 0 90%;
	}
}

script:
function slide(direction) {
	const slider = document.getElementById("slider");

	const slideWidth = this.slider.clientWidth;
	const maxScrollLeft = this.slider.scrollWidth - this.slider.clientWidth;

	if (direction === "prev") {
		if (this.slider.scrollLeft === 0) return;
		this.slider.scrollBy({
			left: -slideWidth,
			behavior: "smooth",
		});
	} else if (direction === "next") {
		if (this.slider.scrollLeft === maxScrollLeft) return;
		this.slider.scrollBy({
			left: slideWidth,
			behavior: "smooth",
		});
	}
}
