style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

body {
	font-family: "Inter", system-ui, -apple-system, sans-serif;
	background: #0a0a0a;
	color: white;
	max-width: 100dvw;
}

.slider-container {
	position: relative;
	height: 100vh;
	display: flex;
	align-items: center;
	overflow: hidden;
}

.slider-track {
	display: flex;
	flex-direction: column;
	justify-content: flex-start;
	max-width: 100%;
	width: 100%;
	height: 100%;
	transition: transform 0.8s ease;
}

.slide {
	min-height: 100%;
	max-height: 100%;
	display: flex;
	align-items: center;
	justify-content: center;
	overflow: hidden;
	position: relative;
}

.slide-content {
	position: relative;
	z-index: 2;
	text-align: center;
	padding: 20px;
	max-width: 800px;
}

.slide-number {
	position: absolute;
	top: 30px;
	left: 30px;
	font-size: 1.2rem;
	opacity: 0.8;
}

.slide-image {
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	object-fit: cover;
	opacity: 0.7;
	transition: opacity 0.8s ease;
}

.slide:hover .slide-image {
	opacity: 0.85;
}

h2 {
	font-size: 3rem;
	margin-bottom: 1rem;
	transform: translateY(30px);
	opacity: 0;
	transition: all 0.6s ease;
}

p {
	font-size: 1.2rem;
	transform: translateY(40px);
	opacity: 0;
	transition: all 0.6s ease 0.1s;
}

.slide.active h2,
.slide.active p {
	transform: translateY(0);
	opacity: 1;
}

.progress-bar {
	position: fixed;
	top: 50%;
	right: 30px;
	transform: translateY(-50%);
	display: flex;
	flex-direction: column;
	gap: 15px;
	z-index: 3;
}

.progress-item {
	width: 6px;
	height: 40px;
	background: rgba(255, 255, 255, 0.4);
	transition: all 0.3s ease;
	cursor: pointer;
	position: relative;
}

.progress-item.active {
	background: rgba(255, 255, 255, 1);
	height: 60px;
}

.progress-item::after {
	content: attr(data-index);
	position: absolute;
	left: 20px;
	top: 50%;
	transform: translateY(-50%);
	opacity: 0;
	transition: opacity 0.3s ease;
}

.progress-item:hover::after {
	opacity: 1;
}

.nav-arrow {
	position: fixed;
	left: 50%;
	transform: translateX(-50%);
	width: 50px;
	height: 50px;
	background: rgba(255, 255, 255, 0.1);
	border: none;
	border-radius: 50%;
	cursor: pointer;
	z-index: 3;
	transition: all 0.3s ease;
	display: flex;
	align-items: center;
	justify-content: center;
}

.nav-arrow:hover {
	background: rgba(255, 255, 255, 0.2);
	transform: translateX(-50%) scale(1.1);
}

.nav-up {
	top: 30px;
}

.nav-down {
	bottom: 30px;
}

@media (max-width: 768px) {
	h2 {
		font-size: 2rem;
	}

	p {
		font-size: 1rem;
	}

	.progress-bar {
		right: 15px;
	}

	.nav-arrow {
		width: 40px;
		height: 40px;
	}
}

@media (max-width: 480px) {
	.slide-number {
		top: 20px;
		left: 20px;
	}

	.progress-bar {
		right: 10px;
		gap: 10px;
	}

	.progress-item {
		height: 30px;
	}

	.progress-item.active {
		height: 40px;
	}
}

script:
const sliderTrack = document.querySelector(".slider-track");
const slides = document.querySelectorAll(".slide");
const progressBar = document.querySelector(".progress-bar");
const navUp = document.querySelector(".nav-up");
const navDown = document.querySelector(".nav-down");
let currentSlide = 0;
let isAnimating = false;

slides.forEach((_, index) => {
	const progressItem = document.createElement("div");
	progressItem.className = "progress-item";
	progressItem.dataset.index = index + 1;
	progressItem.addEventListener("click", () => goToSlide(index));
	progressBar.appendChild(progressItem);
});

const progressItems = document.querySelectorAll(".progress-item");

function updateProgress() {
	progressItems.forEach((item, index) => {
		item.classList.toggle("active", index === currentSlide);
	});
}

function goToSlide(directionOrIndex) {
	if (isAnimating) return;
	isAnimating = true;

	let nextSlide;

	if (typeof directionOrIndex === "string") {
		if (directionOrIndex === "prev" && currentSlide > 0) {
			nextSlide = currentSlide - 1;
		} else if (directionOrIndex === "next" && currentSlide < slides.length - 1) {
			nextSlide = currentSlide + 1;
		} else {
			isAnimating = false;
			return;
		}
	} else if (typeof directionOrIndex === "number") {
		nextSlide = directionOrIndex;
	}

	let offHeight = slides[0].offsetHeight;
	sliderTrack.style.transform = `translateY(-${offHeight * nextSlide}px)`;

	slides.forEach((slide) => slide.classList.remove("active"));
	slides[nextSlide].classList.add("active");

	currentSlide = nextSlide;
	updateProgress();

	setTimeout(() => {
		isAnimating = false;
	}, 800);
}

let scrollBefore = 0;

sliderTrack.addEventListener("wheel", (e) => {
	e.preventDefault();

	if (e.deltaY < 0) {
		goToSlide("prev");
	} else {
		goToSlide("next");
	}
});

navUp.addEventListener("click", () => goToSlide("prev"));
navDown.addEventListener("click", () => goToSlide("next"));
