style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
}

body {
	background: #f0f2f5;
	min-height: 100vh;
	display: flex;
	align-items: center;
}

.slider-container {
	width: 100%;
	max-width: 1200px;
	margin: 0 auto;
	padding: 40px 20px;
	position: relative;
}

.slider-wrapper {
	position: relative;
	overflow: hidden;
	border-radius: 20px;
	box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
}

.slider {
	display: flex;
	transition: transform 0.5s cubic-bezier(0.4, 0, 0.2, 1);
}

.slide {
	min-width: 100%;
	position: relative;
	aspect-ratio: 16/9;
}

.slide img {
	width: 100%;
	height: 100%;
	object-fit: cover;
	object-position: center;
}

.slide-content {
	position: absolute;
	bottom: 0;
	left: 0;
	right: 0;
	padding: 40px;
	background: linear-gradient(transparent, rgba(0, 0, 0, 0.9));
	color: white;
}

.slide-title {
	font-size: 2rem;
	margin-bottom: 1rem;
	opacity: 0;
	transform: translateY(30px);
	transition: all 0.4s ease;
}

.slide.active .slide-title {
	opacity: 1;
	transform: translateY(0);
	transition-delay: 0.2s;
}

.controls {
	position: absolute;
	top: 50%;
	transform: translateY(-50%);
	width: 100%;
	display: flex;
	justify-content: space-between;
	padding: 0 20px;
}

.control-btn {
	background: rgba(255, 255, 255, 0.9);
	border: none;
	width: 50px;
	height: 50px;
	border-radius: 50%;
	cursor: pointer;
	box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
	transition: all 0.3s ease;
	display: flex;
	align-items: center;
	justify-content: center;
}

.control-btn:hover {
	background: white;
	transform: scale(1.1);
}

.progress-bar {
	position: absolute;
	bottom: 20px;
	left: 50%;
	transform: translateX(-50%);
	display: flex;
	gap: 8px;
}

.progress-item {
	width: 40px;
	height: 4px;
	background: rgba(255, 255, 255, 0.3);
	border-radius: 2px;
	overflow: hidden;
}

.progress-fill {
	width: 0%;
	height: 100%;
	background: white;
	transition: width .4s linear;
}

.active .progress-fill {
	width: 100%;
}

@media (max-width: 768px) {
	.slide-title {
		font-size: 1.5rem;
	}

	.control-btn {
		width: 40px;
		height: 40px;
	}
}

script:
const slider = document.querySelector(".slider");
const prevBtn = document.querySelector(".prev");
const nextBtn = document.querySelector(".next");
const progressBar = document.querySelector(".progress-bar");

const slides = [
	{ id: 201, title: "Mountain Adventure" },
	{ id: 202, title: "Ocean Sunset" },
	{ id: 203, title: "City Lights" },
	{ id: 204, title: "Forest Trail" },
	{ id: 206, title: "Desert Sunrise" }
];

let currentSlide = 0;
let autoSlideInterval;

function createSlides() {
	slides.forEach((slide, index) => {
		const slideElement = document.createElement("div");
		slideElement.className = `slide ${index === 0 ? "active" : ""}`;
		slideElement.innerHTML = `
			<img src="https://picsum.photos/id/${slide.id}/1600/900" alt="${slide.title}">
			<div class="slide-content">
				<h2 class="slide-title">${slide.title}</h2>
			</div>
		`;
		slider.appendChild(slideElement);
		const progressItem = document.createElement("div");
		progressItem.className = "progress-item";
		progressItem.innerHTML = '<div class="progress-fill"></div>';
		progressBar.appendChild(progressItem);
	});
}

function updateSlide(index) {
	slider.style.transform = `translateX(-${index * 100}%)`;
	document.querySelectorAll(".slide").forEach(slide => slide.classList.remove("active"));
	slider.children[index].classList.add("active");
	document.querySelectorAll(".progress-fill").forEach(bar => bar.style.width = "0%");
	document.querySelectorAll(".progress-item")[index].querySelector(".progress-fill").style.width = "100%";
}

function nextSlide() { currentSlide = (currentSlide + 1) % slides.length; updateSlide(currentSlide); }
function prevSlide() { currentSlide = (currentSlide - 1 + slides.length) % slides.length; updateSlide(currentSlide); }
function startAutoSlide() { autoSlideInterval = setInterval(nextSlide, 5000); }
function resetAutoSlide() { clearInterval(autoSlideInterval); startAutoSlide(); }

prevBtn.addEventListener("click", () => { prevSlide(); resetAutoSlide(); });
nextBtn.addEventListener("click", () => { nextSlide(); resetAutoSlide(); });

createSlides();
startAutoSlide();
