style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Segoe UI", sans-serif;
}

.slider-container {
	position: relative;
	max-width: 1400px;
	margin: 2rem auto;
	overflow: hidden;
	border-radius: 20px;
	background: rgba(0, 0, 0, 0.8);
	box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
}

.main-slider {
	position: relative;
	height: 70vh;
}

.slide {
	position: absolute;
	width: 100%;
	height: 100%;
	opacity: 0;
	transition: opacity 0.8s ease, transform 0.8s ease;
	transform: scale(0.95);
	pointer-events: none;
}

.slide.active {
	opacity: 1;
	transform: scale(1);
	pointer-events: all;
	z-index: 2;
}

.slide img {
	width: 100%;
	height: 100%;
	object-fit: cover;
	object-position: center;
}

.caption {
	position: absolute;
	bottom: 0;
	left: 0;
	right: 0;
	padding: 2rem;
	background: linear-gradient(transparent, rgba(0, 0, 0, 0.8));
	color: white;
	transform: translateY(100%);
	transition: transform 0.4s ease;
}

.slide.active .caption {
	transform: translateY(0);
}

.controls {
	position: absolute;
	top: 50%;
	transform: translateY(-50%);
	width: 100%;
	display: flex;
	justify-content: space-between;
	padding: 0 1rem;
	z-index: 3;
}

.control-btn {
	background: rgba(255, 255, 255, 0.9);
	border: none;
	width: 50px;
	height: 50px;
	border-radius: 50%;
	cursor: pointer;
	transition: all 0.3s ease;
	display: flex;
	align-items: center;
	justify-content: center;
	box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
}

.control-btn:hover {
	transform: scale(1.1);
	background: #fff;
}

.thumbnails {
	display: flex;
	justify-content: center;
	gap: 1rem;
	padding: 1rem;
	background: rgba(0, 0, 0, 0.8);
}

.thumbnail {
	width: 80px;
	height: 50px;
	border-radius: 5px;
	overflow: hidden;
	cursor: pointer;
	opacity: 0.6;
	transition: all 0.3s ease;
}

.thumbnail.active {
	opacity: 1;
	transform: scale(1.1);
	box-shadow: 0 4px 15px rgba(255, 255, 255, 0.3);
}

.thumbnail img {
	width: 100%;
	height: 100%;
	object-fit: cover;
}

@media (max-width: 768px) {
	.main-slider {
		height: 50vh;
	}

	.thumbnail {
		width: 60px;
		height: 40px;
	}

	.control-btn {
		width: 40px;
		height: 40px;
	}
}

script:
const mainSlider = document.querySelector(".main-slider");
const thumbnails = document.querySelector(".thumbnails");
const prevBtn = document.querySelector(".prev");
const nextBtn = document.querySelector(".next");

const slides = [
	{ id: 401, caption: "Mountain Peak" },
	{ id: 402, caption: "Ocean Waves" },
	{ id: 403, caption: "City Night" },
	{ id: 404, caption: "Autumn Forest" },
	{ id: 405, caption: "Desert Sunset" },
];

let currentIndex = 0;
let autoSlideInterval;

function createSlides() {
	slides.forEach((slide, index) => {
		// Create main slide
		const slideElement = document.createElement("div");
		slideElement.className = `slide ${index === 0 ? "active" : ""}`;
		slideElement.innerHTML = `
			<img src="https://picsum.photos/id/${slide.id}/1400/800" alt="${slide.caption}">
			<div class="caption">${slide.caption}</div>
		`;
		mainSlider.appendChild(slideElement);

		// Create thumbnail
		const thumbnail = document.createElement("div");
		thumbnail.className = `thumbnail ${index === 0 ? "active" : ""}`;
		thumbnail.innerHTML = `
			<img src="https://picsum.photos/id/${slide.id}/200/150" alt="${slide.caption}">
		`;
		thumbnail.addEventListener("click", () => showSlide(index));
		thumbnails.appendChild(thumbnail);
	});
}

function showSlide(index) {
	currentIndex = index;

	// Update main slides
	document.querySelectorAll(".slide").forEach((slide) => {
		slide.classList.remove("active");
	});
	mainSlider.children[index].classList.add("active");

	// Update thumbnails
	document.querySelectorAll(".thumbnail").forEach((thumb) => {
		thumb.classList.remove("active");
	});
	thumbnails.children[index].classList.add("active");
}

function nextSlide() {
	currentIndex = (currentIndex + 1) % slides.length;
	showSlide(currentIndex);
}

function prevSlide() {
	currentIndex = (currentIndex - 1 + slides.length) % slides.length;
	showSlide(currentIndex);
}

function startAutoSlide() {
	autoSlideInterval = setInterval(nextSlide, 6000);
}

function toggleAutoSlide() {
	if (autoSlideInterval) {
		clearInterval(autoSlideInterval);
		autoSlideInterval = null;
	} else {
		startAutoSlide();
	}
}

// Event listeners
prevBtn.addEventListener("click", () => {
	prevSlide();
	toggleAutoSlide();
});

nextBtn.addEventListener("click", () => {
	nextSlide();
	toggleAutoSlide();
});

// Initialize
function init() {
	createSlides();
	startAutoSlide();

	// Pause on hover
	mainSlider.addEventListener("mouseenter", () => clearInterval(autoSlideInterval));
	mainSlider.addEventListener("mouseleave", startAutoSlide);
}

init();
