style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: system-ui, sans-serif;
}

.slider-container {
	position: relative;
	width: 100%;
	max-width: 1400px;
	margin: 2rem auto;
	overflow: hidden;
}

.slider {
	display: flex;
	overflow-x: auto;
	scroll-snap-type: x mandatory;
	scroll-behavior: smooth;
	-ms-overflow-style: none;
	scrollbar-width: none;
}

.slider::-webkit-scrollbar {
	display: none;
}

.slide {
	scroll-snap-align: start;
	flex: 0 0 100%;
	height: 70vh;
	min-height: 500px;
	position: relative;
}

.slide img {
	width: 100%;
	height: 100%;
	object-fit: cover;
	object-position: center;
}

.content {
	position: absolute;
	bottom: 0;
	left: 0;
	right: 0;
	padding: 2rem;
	background: linear-gradient(transparent, rgba(0, 0, 0, 0.9));
	color: white;
	text-align: center;
}

.controls {
	position: absolute;
	top: 50%;
	transform: translateY(-50%);
	width: 100%;
	display: flex;
	justify-content: space-between;
	padding: 0 1rem;
	pointer-events: none;
}

.nav-btn {
	pointer-events: all;
	background: rgba(255, 255, 255, 0.9);
	border: none;
	width: 50px;
	height: 50px;
	border-radius: 50%;
	cursor: pointer;
	transition: all 0.3s ease;
	display: grid;
	place-items: center;
	box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
}

.nav-btn:hover {
	transform: scale(1.1);
	background: #fff;
}

.pagination {
	position: absolute;
	bottom: 1rem;
	left: 50%;
	transform: translateX(-50%);
	display: flex;
	gap: 0.75rem;
	z-index: 2;
}

.pagination-dot {
	width: 12px;
	height: 12px;
	border-radius: 50%;
	background: rgba(255, 255, 255, 0.4);
	border: none;
	cursor: pointer;
	transition: all 0.3s ease;
	position: relative;
	overflow: hidden;
}

.pagination-dot::after {
	content: "";
	position: absolute;
	top: 0;
	left: -100%;
	width: 100%;
	height: 100%;
	background: white;
	transition: left .3s linear;
}

.pagination-dot.active::after {
	left: 0;
}

@media (max-width: 768px) {
	.slide {
		min-height: 400px;
	}

	.nav-btn {
		width: 40px;
		height: 40px;
	}
}

script:
const slider = document.querySelector(".slider");
const prevBtn = document.querySelector(".prev");
const nextBtn = document.querySelector(".next");
const pagination = document.querySelector(".pagination");

const slides = [
	{ id: 501, title: "Alpine Majesty" },
	{ id: 502, title: "Tropical Waters" },
	{ id: 503, title: "Urban Architecture" },
	{ id: 504, title: "Desert Landscape" },
	{ id: 505, title: "Northern Wilderness" },
];

let currentIndex = 0;
let autoScrollTimeout;
let observer;

function createSlides() {
	slides.forEach((slide, index) => {
		const slideElement = document.createElement("div");
		slideElement.className = "slide";
		slideElement.innerHTML = `
			<img src="https://picsum.photos/id/${slide.id}/1600/900" alt="${slide.title}">
			<div class="content">
				<h2>${slide.title}</h2>
			</div>
		`;
		slider.appendChild(slideElement);

		const dot = document.createElement("button");
		dot.className = "pagination-dot";
		dot.addEventListener("click", () => scrollToIndex(index));
		pagination.appendChild(dot);
	});
}

function initIntersectionObserver() {
	observer = new IntersectionObserver(
		(entries) => {
			entries.forEach((entry) => {
				if (entry.isIntersecting) {
					const index = Array.from(slider.children).indexOf(entry.target);
					currentIndex = index;
					updatePagination();
					resetAutoScroll();
				}
			});
		},
		{
			root: slider,
			threshold: 0.5,
		},
	);

	Array.from(slider.children).forEach((slide) => {
		observer.observe(slide);
	});
}

function scrollToIndex(index) {
	const slide = slider.children[index];
	slide.scrollIntoView({
		behavior: "smooth",
		block: "nearest",
		inline: "start",
	});
}

function updatePagination() {
	document.querySelectorAll(".pagination-dot").forEach((dot, index) => {
		dot.classList.toggle("active", index === currentIndex);
	});
}

function nextSlide() {
	currentIndex = (currentIndex + 1) % slides.length;
	scrollToIndex(currentIndex);
}

function prevSlide() {
	currentIndex = (currentIndex - 1 + slides.length) % slides.length;
	scrollToIndex(currentIndex);
}

function autoScroll() {
	autoScrollTimeout = setTimeout(() => {
		nextSlide();
		autoScroll();
	}, 7000);
}

function resetAutoScroll() {
	clearTimeout(autoScrollTimeout);
	autoScroll();
}

// Event listeners
prevBtn.addEventListener("click", prevSlide);
nextBtn.addEventListener("click", nextSlide);
slider.addEventListener("mouseenter", () => clearTimeout(autoScrollTimeout));
slider.addEventListener("mouseleave", autoScroll);

// Initialize
function init() {
	createSlides();
	initIntersectionObserver();
	autoScroll();
	updatePagination();
}

init();
