style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Segoe UI", sans-serif;
}

body {
	background: #f5f5f5;
	min-height: 100vh;
	display: flex;
	align-items: center;
	justify-content: center;
}

.slider-container {
	position: relative;
	width: 100%;
	max-width: 1400px;
	padding: 20px;
}

.main-slide {
	position: relative;
	height: 60vh;
	border-radius: 20px;
	overflow: hidden;
	box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
	margin-bottom: 20px;
}

.main-slide img {
	width: 100%;
	height: 100%;
	object-fit: cover;
	transition: transform 0.3s ease;
}

.thumbnail-track {
	display: grid;
	grid-auto-flow: column;
	gap: 15px;
	padding: 10px;
	overflow-x: auto;
	scroll-snap-type: x mandatory;
	-ms-overflow-style: none;
	scrollbar-width: none;
}

.thumbnail-track::-webkit-scrollbar {
	display: none;
}

.thumbnail {
	scroll-snap-align: start;
	width: 150px;
	height: 100px;
	border-radius: 10px;
	overflow: hidden;
	cursor: pointer;
	opacity: 0.6;
	transition: all 0.3s ease;
	position: relative;
}

.thumbnail.active {
	opacity: 1;
	transform: scale(1.1);
	box-shadow: 0 8px 20px rgba(0, 0, 0, 0.2);
}

.thumbnail img {
	width: 100%;
	height: 100%;
	object-fit: cover;
}

.nav-button {
	position: absolute;
	top: 50%;
	transform: translateY(-50%);
	background: rgba(255, 255, 255, 0.9);
	border: none;
	width: 50px;
	height: 50px;
	border-radius: 50%;
	cursor: pointer;
	display: grid;
	place-items: center;
	box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
	z-index: 2;
	transition: all 0.3s ease;
}

.nav-button:hover {
	transform: translateY(-50%) scale(1.1);
	background: #fff;
}

.prev {
	left: 20px;
}

.next {
	right: 20px;
}

@media (max-width: 768px) {
	.main-slide {
		height: 50vh;
	}

	.thumbnail {
		width: 100px;
		height: 70px;
	}

	.nav-button {
		width: 40px;
		height: 40px;
	}
}

script:
const sliderContainer = document.querySelector(".slider-container");
const mainSlide = document.querySelector(".main-slide");
const thumbnailTrack = document.querySelector(".thumbnail-track");
const prevButton = document.querySelector(".prev");
const nextButton = document.querySelector(".next");
const progressBar = document.querySelector(".progress-bar");

const slides = [
	{ id: 701, title: "Mountain Sunrise" },
	{ id: 702, title: "Beach Paradise" },
	{ id: 703, title: "City Lights" },
	{ id: 704, title: "Forest Trail" },
	{ id: 705, title: "Desert Landscape" },
];

let currentIndex = 0;
let autoSlideInterval;
let isHovering = false;

function createSlides() {
	// Create main slide
	slides.forEach((slide, index) => {
		const img = document.createElement("img");
		img.src = `https://picsum.photos/id/${slide.id}/1600/900`;
		img.alt = slide.title;
		if (index !== 0) img.style.display = "none";
		mainSlide.appendChild(img);
	});

	// Create thumbnails
	slides.forEach((slide, index) => {
		const thumbnail = document.createElement("div");
		thumbnail.className = `thumbnail ${index === 0 ? "active" : ""}`;
		thumbnail.innerHTML = `
			<img src="https://picsum.photos/id/${slide.id}/300/200" alt="${slide.title}">
		`;
		thumbnail.addEventListener("click", () => showSlide(index));
		thumbnailTrack.appendChild(thumbnail);
	});
}

function showSlide(index) {
	currentIndex = index;

	// Update main slide
	mainSlide.querySelectorAll("img").forEach((img, i) => {
		img.style.display = i === index ? "block" : "none";
	});

	// Update thumbnails
	document.querySelectorAll(".thumbnail").forEach((thumb, i) => {
		thumb.classList.toggle("active", i === index);
	});

	// Scroll thumbnail into view
	thumbnailTrack.children[index].scrollIntoView({
		behavior: "smooth",
		block: "nearest",
		inline: "center",
	});
}

function nextSlide() {
	currentIndex = (currentIndex + 1) % slides.length;
	showSlide(currentIndex);
}

function prevSlide() {
	currentIndex = (currentIndex - 1 + slides.length) % slides.length;
	showSlide(currentIndex);
}

// Event listeners
prevButton.addEventListener("click", () => {
	prevSlide();
	clearInterval(autoSlideInterval);
});

nextButton.addEventListener("click", () => {
	nextSlide();
	clearInterval(autoSlideInterval);
});

sliderContainer.addEventListener("mouseenter", () => {
	isHovering = true;
	clearInterval(autoSlideInterval);
});

sliderContainer.addEventListener("mouseleave", () => {
	isHovering = false;
});

createSlides();
