style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

:root {
	--bg-color: #f5f5f5;
	--text-color: #333;
	--icon-bg: #fff;
	--icon-color: #333;
	--icon-hover-bg: #333;
	--icon-hover-color: #fff;
}

.dark-theme {
	--bg-color: #333;
	--text-color: #f5f5f5;
	--icon-bg: #444;
	--icon-color: #f5f5f5;
	--icon-hover-bg: #f5f5f5;
	--icon-hover-color: #333;
}

body {
	background-color: var(--bg-color);
	color: var(--text-color);
	font-family: Arial, sans-serif;
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;
	min-height: 100vh;
	transition: background-color 0.3s ease, color 0.3s ease;
	padding: 20px;
	text-align: center;
}

.theme-toggle-btn {
	position: absolute;
	top: 20px;
	right: 20px;
	padding: 10px 20px;
	border: none;
	background-color: var(--icon-bg);
	color: var(--icon-color);
	border-radius: 5px;
	cursor: pointer;
	transition: background-color 0.3s ease, color 0.3s ease;
}

.theme-toggle-btn:hover {
	background-color: var(--icon-hover-bg);
	color: var(--icon-hover-color);
}

.social-icons {
	display: flex;
	gap: 20px;
	margin-top: 40px;
}

.social-icons a {
	display: flex;
	align-items: center;
	justify-content: center;
	width: 60px;
	height: 60px;
	background-color: var(--icon-bg);
	color: var(--icon-color);
	border-radius: 50%;
	text-decoration: none;
	font-size: 24px;
	transition: background-color 0.3s ease, color 0.3s ease, transform 0.3s ease;
}

.social-icons a:hover {
	background-color: var(--icon-hover-bg);
	color: var(--icon-hover-color);
	transform: scale(1.1);
}

@media (max-width: 600px) {
	.social-icons {
		gap: 15px;
	}
	.social-icons a {
		width: 50px;
		height: 50px;
		font-size: 20px;
	}
}
