style:
:root {
	--bg: #f8f9fa;
	--text: #212529;
	--dock-bg: rgba(255, 255, 255, 0.9);
	--icon-bg: #e9ecef;
	--hover-bg: #dee2e6;
}

[data-theme="dark"] {
	--bg: #1a1a1a;
	--text: #f8f9fa;
	--dock-bg: rgba(25, 25, 25, 0.9);
	--icon-bg: #2d2d2d;
	--hover-bg: #3d3d3d;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	transition: all 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94);
}

body {
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	background: var(--bg);
	font-family: "Segoe UI", sans-serif;
}

.social-dock {
	display: flex;
	gap: 1.5rem;
	padding: 1.5rem;
	background: var(--dock-bg);
	border-radius: 16px;
	backdrop-filter: blur(10px);
	box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
}

.social-item {
	width: 48px;
	height: 48px;
	display: flex;
	align-items: center;
	justify-content: center;
	border-radius: 12px;
	background: var(--icon-bg);
	text-decoration: none;
	position: relative;
	cursor: pointer;
}

.social-item i {
	font-size: 1.2rem;
	color: var(--text);
}

.social-item:hover {
	background: var(--hover-bg);
	transform: translateY(-4px) scale(1.1);
}

.social-item::after {
	content: attr(aria-label);
	position: absolute;
	bottom: -28px;
	background: var(--text);
	color: var(--bg);
	padding: 4px 8px;
	border-radius: 4px;
	font-size: 0.75rem;
	opacity: 0;
	pointer-events: none;
	white-space: nowrap;
}

.social-item:hover::after {
	opacity: 1;
	transform: translateY(-4px);
}

.theme-toggle {
	position: fixed;
	top: 24px;
	right: 24px;
	width: 48px;
	height: 48px;
	border-radius: 50%;
	background: var(--icon-bg);
	border: none;
	cursor: pointer;
	display: flex;
	align-items: center;
	justify-content: center;
}

.theme-toggle i {
	font-size: 1.2rem;
	color: var(--text);
}

@media (max-width: 480px) {
	.social-dock {
		flex-direction: column;
		padding: 1rem;
		border-radius: 24px;
	}

	.social-item {
		width: 40px;
		height: 40px;
	}
}

script:
function toggleTheme() {
	if (document.body.getAttribute("data-theme") === "dark") {
		document.body.removeAttribute("data-theme");
	} else {
		document.body.setAttribute("data-theme", "dark");
	}
	const icon = document.querySelector(".theme-toggle i");
	icon.classList.toggle("fa-moon");
	icon.classList.toggle("fa-sun");
}
