style:
:root {
	--bg: #f0f2f5;
	--text: #2d3436;
	--orbital-bg: rgba(255, 255, 255, 0.95);
	--accent: #4a90e2;
}

[data-theme="dark"] {
	--bg: #1a1a1a;
	--text: #ffffff;
	--orbital-bg: rgba(30, 30, 30, 0.95);
	--accent: #7fb6ff;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	transition: all 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94);
}

body {
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	background: var(--bg);
	font-family: "Inter", sans-serif;
}

.orbital-menu {
	position: relative;
	width: 300px;
	height: 300px;
	display: flex;
	justify-content: center;
	align-items: center;
}

.menu-toggle {
	position: absolute;
	width: 60px;
	height: 60px;
	background: var(--accent);
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	cursor: pointer;
	z-index: 2;
	box-shadow: 0 10px 25px rgba(0, 0, 0, 0.1);
}

.menu-toggle i {
	color: white;
	font-size: 1.5rem;
}

.social-item {
	position: absolute;
	width: 50px;
	height: 50px;
	background: var(--orbital-bg);
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	text-decoration: none;
	box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
	transform: rotate(0deg) translateX(120px);
	opacity: 0;
	visibility: hidden;
}

.social-item i {
	color: var(--text);
	font-size: 1.2rem;
}

.orbital-menu.active .social-item {
	opacity: 1;
	visibility: visible;
}

.social-item:hover {
	background: var(--accent);
}

.social-item:hover i {
	color: white;
}

.theme-switch {
	position: fixed;
	top: 30px;
	right: 30px;
	width: 40px;
	height: 40px;
	border-radius: 50%;
	background: var(--orbital-bg);
	border: none;
	cursor: pointer;
	display: flex;
	align-items: center;
	justify-content: center;
	box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
}

.theme-switch i {
	color: var(--text);
	font-size: 1.2rem;
}

@media (max-width: 480px) {
	.orbital-menu {
		transform: scale(0.8);
	}
}

script:
function toggleMenu() {
	const orbitalMenu = document.querySelector(".orbital-menu");
	const items = document.querySelectorAll(".social-item");

	orbitalMenu.classList.toggle("active");
	items.forEach((item, index) => {
		const angle = index * 72 + 180;
		if (orbitalMenu.classList.contains("active")) {
			item.style.transform = `rotate(${angle}deg) translateX(120px) rotate(-${angle}deg)`;
		} else {
			item.style.transform = "rotate(0deg) translateX(120px)";
		}
	});
}

function toggleTheme() {
	if (document.body.getAttribute("data-theme") === "dark") {
		document.body.removeAttribute("data-theme");
	} else {
		document.body.setAttribute("data-theme", "dark");
	}
	const icon = document.querySelector(".theme-switch i");
	icon.classList.toggle("fa-moon");
	icon.classList.toggle("fa-sun");
}
