style:
:root {
	--bg: #f0f2f5;
	--text: #2d3436;
	--menu-bg: #ffffff;
	--accent: #4a90e2;
	--bloom-base: #e0e5ec;
}

.dark-theme {
	--bg: #1a1a1a;
	--text: #ffffff;
	--menu-bg: #2d2d2d;
	--accent: #7fb6ff;
	--bloom-base: #3d3d3d;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
}

body {
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	background: var(--bg);
	font-family: "Inter", sans-serif;
}

.menu-container {
	position: relative;
	width: 120px;
	height: 120px;
}

.menu-toggle {
	position: absolute;
	width: 60px;
	height: 60px;
	background: var(--menu-bg);
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	cursor: pointer;
	z-index: 2;
	left: 30px;
	top: 30px;
	box-shadow: 0 0 20px rgba(0, 0, 0, 0.1);
}

.menu-toggle i {
	font-size: 1.8rem;
	color: var(--text);
	transition: transform 0.3s;
}

.menu-active .menu-toggle i {
	transform: rotate(135deg);
}

.social-item {
	position: absolute;
	width: 50px;
	height: 50px;
	background: var(--menu-bg);
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	transform: translate(35px, 35px);
	opacity: 0;
	visibility: hidden;
	cursor: pointer;
	clip-path: polygon(50% 50%, 50% 50%, 50% 50%, 50% 50%);
}

.social-item i {
	font-size: 1.4rem;
	color: var(--text);
	transition: all 0.3s;
}

.menu-active .social-item {
	text-decoration: none;
	opacity: 1;
	visibility: visible;
	clip-path: polygon(0 0, 100% 0, 100% 100%, 0 100%);
}

.social-item:hover {
	background: var(--accent);
	box-shadow: 0 0 15px rgba(127, 182, 255, 0.3);
}

.social-item:hover i {
	color: var(--menu-bg);
	transform: rotate(360deg);
}

.theme-switch {
	position: fixed;
	top: 30px;
	right: 30px;
	width: 50px;
	height: 50px;
	border-radius: 15px;
	background: var(--menu-bg);
	border: none;
	cursor: pointer;
	display: flex;
	align-items: center;
	justify-content: center;
	box-shadow: 0 0 15px rgba(0, 0, 0, 0.1);
}

.theme-switch i {
	font-size: 1.4rem;
	color: var(--text);
}

.bloom-effect {
	position: absolute;
	width: 120px;
	height: 120px;
	background: var(--bloom-base);
	border-radius: 50%;
	opacity: 0;
	transform: scale(0);
	transition: all 0.6s;
}

.menu-active .bloom-effect {
	opacity: 0.4;
	transform: scale(2);
}

@media (max-width: 480px) {
	.menu-container {
		transform: scale(0.8);
	}
}

script:
const menuToggle = document.getElementById("menuToggle");
const menuContainer = document.querySelector(".menu-container");
const socialItems = document.querySelectorAll(".social-item");
const themeSwitch = document.getElementById("themeSwitch");
const body = document.body;

menuToggle.addEventListener("click", () => {
	menuContainer.classList.toggle("menu-active");
	const radius = 100;
	const angleStep = (Math.PI * 1.5) / socialItems.length;

	socialItems.forEach((item, index) => {
		if (menuContainer.classList.contains("menu-active")) {
			const angle = angleStep * index;
			const x = radius * Math.cos(angle);
			const y = radius * Math.sin(angle);
			item.style.transform = `translate(${x + 35}px, ${y + 35}px)`;
			item.style.transitionDelay = `${index * 0.1}s`;
		} else {
			item.style.transform = "translate(35px, 35px)";
			item.style.transitionDelay = "0s";
		}
	});
});

function toggleTheme() {
	body.classList.toggle("dark-theme");
	const isDark = body.classList.contains("dark-theme");
	themeSwitch.innerHTML = isDark ? '<i class="fas fa-sun"></i>' : '<i class="fas fa-moon"></i>';
	localStorage.setItem("theme", isDark ? "dark" : "light");
}

const savedTheme = localStorage.getItem("theme");
const systemDark = window.matchMedia("(prefers-color-scheme: dark)").matches;

if (savedTheme === "dark" || (!savedTheme && systemDark)) {
	body.classList.add("dark-theme");
	themeSwitch.innerHTML = '<i class="fas fa-sun"></i>';
}

themeSwitch.addEventListener("click", toggleTheme);
